css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animelyrics.com") {
    /* ad and bloat killer */
    
    .adsbygoogle,
    .highlight, .highlight + br + object,
    ul#crumbs + center,
    script[src*='pagead2.google'] + ins,
    #songbox, .utility2, #ytb, hr {
        display: none !important;
        visibility: hidden !important;
    }
    
    /* General Style Overrides */
    
    * {
        font-family: "Open Sans", sans-serif;
        text-decoration: none !important;
        color: #ccc;
    }
    
    /* Texts */
    
    a, a:visited, #content a, #tab-nav1 #nav-sec1 a, #utility li a, #tab-nav1 a,
    #crumbs li a:link, #crumbs li, #tab-subnav1 ul #subnav-sec1 a {
        color: #0080ff;
    }
    
    a:hover, #tab-nav1 #nav-sec1 a:hover, #utility li a:hover, #tab-nav1 a:hover,
    a:link, #crumbs li a:hover, #crumbs li a:focus, #tab-subnav1 ul #subnav-sec1 a:hover {
        color: #80c0ff;
    }
    
    #content h1 {
        color: #fff;
    }
    
    .lyrics {
        color: #ccc;
    }
    
    /* Images */
    
    /* Backgrounds */
    
    #crumbs li a {
        background: none;
    }
    
    #utility, body,
    #tab-nav1 li#nav-sec1, #tab-nav1 #nav-sec1 a,
    #tab-nav1 li#nav-sec2, #tab-nav1 #nav-sec2 a,
    #tab-nav1 li#nav-sec3, #tab-nav1 #nav-sec3 a,
    #tab-nav1 li#nav-sec4, #tab-nav1 #nav-sec4 a,
    #tab-nav1 li#nav-sec5, #tab-nav1 #nav-sec5 a,
    #tab-nav1 li#nav-sec6, #tab-nav1 #nav-sec6 a,
    #tab-nav1 li#nav-sec7, #tab-nav1 #nav-sec7 a,
    #tab-nav1 li#nav-sec8, #tab-nav1 #nav-sec8 a,
    #tab-nav1 li#nav-sec9, #tab-nav1 #nav-sec9 a,
    #tab-nav1 li#nav-sec10, #tab-nav1 #nav-sec10 a,
    #tab-nav1 li#nav-sec11, #tab-nav1 #nav-sec11 a, #crumbs li {
        background: #222;
    }
    
    #navi, #logo a, #crumbs li a {
        background: #333;
    }
    
    #tab-subnav1 {
        background: #444;
    }
    
    /* Borders */
    
    #tab-subnav1 {
        border-top: 1px solid;
        border-bottom: 1px solid;
    }
    
    #utility, #utility li, #crumbs,
    #tab-nav1 li, #tab-subnav1 {
        border-color: rgba(255,255,255,0.1);
    }
    
    /* Box Shadows */
    
    #utility {
        box-shadow: 0 0 0 3px rgba(255,255,255,0.1);
    }
    
    /* Specific Overrides */
    
    #utility {
        position: fixed;
        top: 0;
        right: 0;
        z-index: 100;
    }
    
    #logo a {
        position: absolute;
        display: block;
        top: -35px;
        left: -10px;
        font-size: 90px;
        color: #666;
        white-space: nowrap;
        background: none;
        text-transform: uppercase;
    }
    
    #logo a:before {
        content: " ";
        display: block;
    }
    
    #tab-subnav1 ul li {
        font-size: 0;
    }
    
    #tab-subnav1 ul #subnav-sec1 a {
        font-size: 12px;
        padding: 2px 5px;
        background: #222;
    }
    
    #crumbs {
        margin-top: 25px;
    }
    
    #crumbs li a {
        padding: 0 10px;
        border-left: 1px solid rgba(255,255,255,0.1);
        border-right: 1px solid rgba(255,255,255,0.1);
    }
    
    #crumbs + center + table tbody tr:first-of-type td:first-of-type {
        width: 100%;
    }
    
    .fb_iframe_widget {
        position: absolute;
        top: 80px;
        right: 0;
        padding: 10px;
        background: #333;
        border: 1px solid rgba(255,255,255,0.1);
    }
    
    #cse-search-box {
        position: absolute;
        top: 160px;
        right: 0;
        width: 472px;
    }
    
    #cse-search-box div input[type=text] {
        width: 363px;
        background: #222 !important;
        border: 1px solid rgba(255,255,255,0.1) !important;
    }
    
    #cse-search-box div input[type=submit] {
        width: 100px;
        background: #222 !important;
        border: 1px solid rgba(255,255,255,0.1) !important;
    }
    
    #cse-search-box div input[type=submit]:hover {
        width: 100px;
        background: #333 !important;
        border: 1px solid rgba(255,255,255,0.1) !important;
    }
    
    a[href*='.jis'] {
        position: absolute;
        top: 200px;
        right: 336px;
        padding: 5px 25px;
        background: #333;
        border: 1px solid rgba(255,255,255,0.1);
    }
    
    a[href*='.jis']:hover {
        background: #444;
    }
    
    #content h1 {
        margin-top: -80px;
        margin-bottom: -50px;
        font-size: 50px;
    }
    
    #content h1 ~ table {
        width: 100%;
        margin-top: -100px;
        padding: 25px;
        background: #333;
        border: 1px solid rgba(255,255,255,0.1);
    }
    
    #content h1 ~ table td {
        width: 50% !important;
    }
    
    #content h1 ~ table tr {
        text-align: center;
    }
    
    #content h1 ~ table tr:nth-of-type(1) {
        font-size: 25px;
        height: 100px;
    }
    
    #content h1 ~ table tr:nth-of-type(n+2) td {
        font-size: 15px;
    }
    
    td.romaji {
        border-color: #0080ff;
    }
    
    .mytable {
        margin-top: 30px;
    }
    
    .mytable th {
        background: #222;
        border-color: rgba(255,255,255,0.1);
        text-align: center;
        font-size: 25px;
    }
    
    .mytable th.specalt, .mytable td.alt,
    .mytable th.spec, .mytable td {
        background: #222;
        border-color: rgba(255,255,255,0.1);
        text-align: center;
        color: #fff;
    }
    
    #content a[href*='/index.php?action=submit'] {
        display: block;
        margin-top: 20px;
    }
    
    a[href*='/index.php?action=submit'] ~ table {
        position: relative;
        top: 150px;
    }
    
    #tab-subnav1 {
        height: 23px;
        font-size: 0;
    }
    
    #tab-subnav1 > input {
        display: none;
    }
    
    input, select {
        background: #222;
        border-color: rgba(255,255,255,0.1) !important;
    }
}
