css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habrahabr.ru") {
div#layout {
	max-width: none !important;
}
#navbar .nav_panel {
	left: -10em;
	transition: left .7s ease;
}
#navbar:hover .nav_panel {
	left: 0;
}
#navbar .logo {
	background-image: url(http://188.40.78.19/uploads/old_logo_navbar.png) !important;
	background-position: 0 0 !important;
	background-size: 71px 71px !important;
}

}
