css:
@-moz-document url("http://shazoo.ru/"), url("https://shazoo.ru/") {
.tabsHeader, .homeRight  {
	display: none;
}
  
#home .homeLeft, #home .homeCenter {
	width: 40%;
}

#home .homeLeft{
  margin-left: 10%;
}

#home .homeLeft .entryByline{
    display: block;
    padding: 3px;
    font-size: 14px;
}
  
#home .homeLeft .entry h2{
	font-size: 16px;
}
  
#home .homeLeft .entry.unread{
    border-left: 5px solid rgba(0,141,222,1);
}

#home .homeLeft .entry{
  	padding: 15px;
}
#home .homeLeft .entryImage.desktop img{
    padding-top: 5px;
}
  
.homeCenter .topEntry h2 {
  font-size: 16px;
  font-weight: bold;
  line-height: 1.0;
}

.homeDouble{
  width: 100%;
}
  
.entryList .entry .entryByline{
  font-size: 14px;
}

.homeLeft .firstEntry .entryImage.mobile img{
  height: auto;
  max-height: 100%;
}

#home .homeLeft .entry.popular {
  background-image: none;
}
}

@-moz-document url-prefix("https://shazoo.ru/2"), url-prefix("https://shazoo.ru/t"), url-prefix("https://shazoo.ru/ga"), url-prefix("https://shazoo.ru/o"), url-prefix("https://shazoo.ru/a"), url-prefix("https://shazoo.ru/b"), url-prefix("https://shazoo.ru/s") {
#aside, .sb {
  display: none;
}

.main #contentContainer{
  width: 70%;
  margin-left: 15%;
}

section.body, section.lead{
  text-align: justify;
}

.entryHeaderContainer.wide:not(.tall) .entryImage, .entryHeaderContainer.wide:not(.tall):not(.contain), .entryHeaderContainer.wide:not(.tall):not(.contain), .entryHeaderContainer.wide:not(.tall):not(.contain) .entryImage{
  height: 200px;       
}
      
.entryList .entry{
  border: 1px solid rgba(0,0,0,.04);
  background-color: #fafafa;
  padding: 15px;
  -webkit-box-shadow: 0px 3px 2px 0px rgba(79, 79, 79, 0.5);
  -moz-box-shadow:    0px 3px 2px 0px rgba(79, 79, 79, 0.5);
  box-shadow:         0px 3px 2px 0px rgba(79, 79, 79, 0.5);
}

#replyDialog{
  max-width: 400px;
}
}
