css:
@-webkit-document domain(bogleheads.org), url-prefix(http://www.bogleheads.org) {
	body {
		font-family: "Segoe UI" !important;
		font-size: 1em;
		color: #323232 !important;
	}
	.content {
		font-family: "Segoe UI" !important;
		font-size: 14px !important;
		color: #323232 !important;
	}
	a {
		text-decoration: none !important;
		color: #009bff !important;
	}

	/* Hide rounded corners on post page 
	.corners-top, .corners-bottom
	{
		display:none !important;
	}*/

	/* Collapse all table borders */
	table, td, th
	{
		border-collapse:collapse;
	}

	/* Change content table border */
	#table_content
	{
		border: 1px solid #A1A1A1 !important;
		padding: 0 !important;
	}

	/* Remove sidebar tables borders */
	.sidebar_table
	{
		border: 0 !important;
		padding: 0 !important;
	}

	/* Make the header fixed on top, and push the body down */
	#header {
		position: fixed;
		top:0;
		background-color: white;
		width: 100%;
	}
	#table_body {
		margin-top: 100px;
	}

	/* Make the sidebar fixed, and to the left */
	#sidebar {
		position: fixed;
		top:100;
		left:10;
		background-color: #fbfbfb !important;
	}

	#table_header tbody tr td:nth-child(2) a[href="index.php"] {
		visibility: hidden;
	}
	#table_header tbody tr td:nth-child(2) a[href="index.php"]:before {
		content: "bogleheads";
		visibility: visible;
		font-size: 2.5em;
		color: #323232 !important;
	}


	#container {
		max-width: 95% !important;
	}
	#table_content table td {
		padding: 8px !important;
	}
	#table_content table tr:nth-child(odd)
	{
		background:#EDEDED;
	}
	#table_content table tr td:nth-child(1)
	{
		text-align: center;
	}
	#table_content table tr td:nth-child(2)
	{
		display:none;
	}
        
        /* FORUM STYLES */
        #wrap {
                min-width:650px;
                width: 960px;
                margin-left:auto;
                margin-right:auto;
        }
        .bg1 {
                background-color: rgba(94,226,255,0.1);
        }
        .bg2 {
                background-color: rgba(94,226,255,0.5);
        }

        blockquote {
                background-color: #EAEAEA;
        }
        h2 {
                font-family: "Segoe UI" !important;
        }
        .postbody h3 {
                font-family: "Segoe UI" !important;
                font-size: 1em;
        }
        p.author a:nth-child(1) {
                display:none;
        }
        p.author {
                margin-left:-30px;
                overflow: hidden;
                color: #FFF;
                font-size: 0.7em;
        }
        .signature {
                display:none;
        }
}
