css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://twitter.com/") {
    @font-face {
        font-family: 'iran';
        font-style: normal;
        font-weight: normal;
        src: local('Iran'), local('Iran'), url(http://bolhasani.com/fonts/iran_sans.woff) format('woff');
}
    
    .tweet-text-rtl {
        font-family: tahoma;
        font-size: 0.87em;
        -webkit-font-smoothing: antialiased;
        line-height: 1.5;
    }
    
    .dashboard .tweet-content .tweet-box[dir="rtl"] {
        font-family: tahoma;
        font-size: 0.9em;
        -webkit-font-smoothing: antialiased;
        line-height: 1.5;
    }
    
    .avatar, .DashboardProfileCard-avatarLink, .DashboardProfileCard-avatarImage, .conversation-module .conversation-tweet-item .stream-item-header .avatar, .ProfileAvatar, .ProfileAvatar-image, .ProfileAvatar-placeholderImage, .ProfileCardMini-avatarImage, .ProfileTweet-avatar {
        border-radius: 100px;
    }
    
    .ProfileCanopy-avatar {
        left: 40px;
        bottom: -107px;
        transition: all 0.3s ease-in-out;
    }
    
    .ProfileAvatar {
        width: 150px;
        height: 150px;
        transition: all 0.3s ease-in-out;
    }
    
    .ProfileAvatar:hover {
        transform: translateY(-10px)
    }
    
    .DashboardProfileCard {
        border: none;
    }
    
    .DashboardProfileCard-bg {
        border-bottom: none;
        border-radius: 5px;
    }
    
    .DashboardProfileCard {
        background: transparent;
    }
    
    .tweet-user {
        border-radius: 5px;
    }
    
    .module .flex-module {
        background-color: rgba(0, 0, 0, 0);
        border: medium none;
        line-height: 16px;
        padding: 15px;
    }
    
    .DashboardProfileCard-stats {
        border-top: 1px solid #E1EBF2;
        border-bottom: 1px solid #E1EBF2;
        margin: 4em 0 15px;
    }
    
    .trends {
        border-top: 1px solid #E1EBF2;
        border-bottom: 1px solid #E1EBF2;
        margin: 20px 0;
        opacity: 0.7;
        transition: all 0.3s ease-in-out;
    }
    
    .trends:hover {
        opacity: 1;
    }
    
    .follower-request-link {
        margin-left: 15px;
        opacity: 0.7;
        transition: all 0.3s ease-in-out;
    }
    
    .follower-request-link:hover {
        opacity: 1;
    }
    
    .DashboardProfileCard-content {
        margin-top: 0;
        opacity: 1;
    }
    
    .DashboardProfileCard-userFields {
        margin-top: 2.2em;
        text-align: center;
        left: 45px
    }
    
    .DashboardProfileCard-avatarLink {
        background-color: #FFFFFF;
        margin: -3em 0 0 8px;
        padding: 3px 3px 1px 3px;
        vertical-align: bottom;
        position: relative;
        left: 6.4em;
        transition: all 0.3s ease-in-out;
    }
    
    .DashboardProfileCard-avatarLink:hover {
        transform: translateY(-5px)
    }
    
    .home-tweet-box {
        background: rgba(255, 255, 255, 0.7) !important;
        border: none;
    }
    
    .dashboard-left {
        transition: all 0.3s ease-in-out;
    }
    
    .ProfileHeaderCard {
        margin-top: 60px;
    }
    
    .ProfileTweet-text[dir="rtl"] {
        font-family: 'iran';
        line-height: 1.6;
        font-size: 1.2em;
        padding: 10px 0;
    }
    
    .activity-truncated-tweet {
        font-family: 'iran';
        line-height: 1.5;
    }
    
    .latest-tweet-timestamp {
        font-family: Helvetica;
    }
    
    .activity-truncated-tweet .tweet-text {
        font-size: 0.9em;
    }
    
    .permalink-tweet p.tweet-text-rtl {
        font-family: 'iran';
        font-size: 1.6em;
    }
    
    .stream-item, .unfocusable-stream-item {
        border-left: 1px solid #F4F4F4;
        border-right: 1px solid #F4F4F4;
    }
    
    .tweet {
        border-bottom: 1px solid #F4F4F4;
    }
    
    .content-header .header-inner, .content-no-header .no-header-inner {
        border-bottom: 1px solid #f4f4f4;
    }
    
    .content-header, .content-no-header {
        border-color: #f4f4f4
    }
}
