css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){*{/*$*/}
  #fancybox-overlay {
    display: block;
    bottom: 10px;
    left: 15px;
    top: auto;
    right: auto;
    max-width: 500px;
    background: #444;
    border: 1px solid #666;
    text-align: left;
    padding: 10px;
    box-shadow: 0px 0px 15px #666;
  }

  #fancybox-overlay:before {
    content: "[Stylish] MAL: Dark and Flat is now being maintained and updated by Ryan Slone. Please follow this link to install the updated version:";
    border-left: 3px solid #08f;
    padding: 5px 0px 5px 10px;
    color: #eee;
    font-size: 14px;
    line-height: 2;
  }

  #fancybox-overlay:after {
    content: "https://goo.gl/oxJR5p";
    font-family: "DejaVu Sans Mono", "Source Code Pro", "Menlo", monospace;
    display: block;
    font-weight: bold;
    padding: 5px 30px;
    font-size: 16px;
    color: #0af;
  }
}
