css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('tumblr.com') {
  #container {
    background: -moz-linear-gradient(left, #5d463e 0%, #674d44 25%, #674d44 75%, #5d463e 100%) !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #5d463e), color-stop(25%, #674d44), color-stop(75%, #674d44), color-stop(100%, #5d463e)) !important;
    background: linear-gradient(to right, #5d463e 0%, #674d44 25%, #674d44 75%, #5d463e 100%) !important;
  }
  #search_form .search_form_row {
    background: #826257 !important;
  }
  .search_subheading {
    background: #644b42 !important;
  }
  .search_results_section:first-child .search_subheading {
    border-bottom-color: #5d463e !important;
  }
  .search_popover .search_subheading {
    border-top-color: #5d463e !important;
    border-bottom-color: #5d463e !important;
  }
  .search_results_section:first-child .search_subheading:after {
    border-bottom-color: #644b42 !important;
  }
  #tabs_outer_container .selection_nipple {
    display: none !important;
  }
  .l-content {
    background: transparent !important;
  }
  .bluthSkin td.mceToolbar {
    background: #d1c0ba !important;
  }
  #post_content .main_content {
    border-color: #8b695d !important;
  }
  .mceIframeContainer,
  .tag_editor {
    border-top-color: #8b695d !important;
  }
  .search_endpoint .popover_header {
    background: #73564d !important;
    color: #ffffff !important;
  }
  .popover_menu_item,
  .popover_menu_item a {
    color: #e6e6e6 !important;
  }
  .post_control.delete:hover,
  .popover_menu_item:hover a,
  .popover_menu_item.keyboard_focus a {
    color: #644b42 !important;
  }
  .popover_gradient > .popover_inner {
    box-shadow: 0 1px 0 rgba(146, 109, 97, 0.75) inset;
  }
  .popover_gradient > .popover_inner,
  .search_popover > .popover_inner {
    background: #826257 !important;
    color: #ffffff !important;
  }
  .popover_options li a {
    color: #ffffff !important;
  }
  .popover_options li a:hover {
    color: #644b42 !important;
    background: #ffffff !important;
  }
  body#tinymce {
    background: #73564d !important;
  }
  body#tinymce p {
    color: #ffffff !important;
  }
  .popover_gradient:after,
  .popover_menu_item:first-child:after {
    border-bottom-color: #826257 !important;
  }
  .advanced_post_options .option {
    border-top-color: #926d61 !important;
  }
  .label_field {
    background: #5d463e !important;
    border-right-color: #926d61 !important;
    box-shadow: none !important;
  }
  .popover_post_options .option {
    text-shadow: none !important;
  }
  .advanced_post_options .option .label_field {
    border-color: #926d61 !important;
  }
  .advanced_post_options input[type="text"] {
    text-shadow: none !important;
  }
  .advanced_post_options .full_row {
    box-shadow: none !important;
  }
  #audio_search_results {
    border-top-color: #826257 !important;
  }
  #audio_search_results ol li {
    border-bottom-color: #826257 !important;
  }
  #audio_search_results .audio_label,
  #audio_search_results .audio_label a,
  #audio_search_results .audio_label span {
    color: #ffffff !important;
  }
  #audio_search_results ol li:hover {
    background: #8e6b5f !important;
  }
  .post_permalink:before {
    border-right-color: #674d44 !important;
  }
  .l-container.l-container--two-column-dashboard .left_column {
    background: transparent !important;
  }
  #right_column {
    background: #45342e;
    margin-top: 2px !important;
    padding-bottom: 0 !important;
  }
  #right_column:after {
    display: none !important;
  }
  .l-container--two-column .right_column,
  .l-container--two-column-dashboard .right_column {
    background: transparent !important;
  }
  #right_column #dashboard_controls_open_blog {
    position: relative !important;
  }
  #right_column #dashboard_controls_open_blog #popover_blogs {
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
  }
  #popover_blogs {
    -webkit-box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0) !important;
    -moz-box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0) !important;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0) !important;
  }
  #popover_blogs .popover_inner {
    background: #826257 !important;
    border: 1px solid #644b42 !important;
  }
  #popover_blogs .popover_inner > div,
  #popover_blogs .popover_inner > div span,
  #popover_blogs .popover_inner > div small {
    color: #ffffff !important;
  }
  #popover_blogs .popover_inner > div:hover {
    background: #644b42 !important;
  }
  .controls_section .section_header {
    border-bottom-width: 0 !important;
  }
  .follow_list.recommended_tumblelogs,
  .follow_list.trending_tumblelogs {
    max-height: 240px !important;
  }
  .user_list li .follow_list_item_blog:before,
  .radar_footer .radar_avatar:before {
    display: none !important;
  }
  .radar_footer.has_follow_button .radar_avatar {
    width: 180px !important;
  }
  .radar .radar_footer .follow {
    right: 5px !important;
  }
  .ui_follow,
  .controls_section .item .follow,
  .radar_footer .follow {
    color: #ffffff !important;
  }
  div.ui_follow,
  .controls_section li.item .follow {
    right: 4px !important;
  }
  .radar_footer .follow {
    right: 5px !important;
  }
  .controls_section .item:hover .icon_plus {
    color: #644b42 !important;
  }
  div.ui_follow:hover,
  .controls_section li .follow:hover,
  .radar_footer .follow:hover {
    background: #bda49c !important;
  }
  #main_help.help_search {
    background: transparent !important;
    border-width: 0 !important;
  }
  #main_doc_page #container {
    padding-bottom: 20px !important;
  }
  #main_doc_page #container #content {
    background: #ffffff !important;
  }
  #main_doc_page #container {
    padding-bottom: 20px !important;
  }
  #main_doc_page #container #content {
    background: #ffffff !important;
  }
  #tabs {
    background: transparent !important;
    border-color: #644b42 !important;
  }
  #tabs .tab {
    border-color: #644b42 !important;
    background: #826257 !important;
    color: #ffffff !important;
  }
  #tabs .tab.selected {
    background: #9e7a6e !important;
  }
  .post_avatar:after {
    display: none !important;
  }
  #dashboard_useraction_following .follower .name a,
  #dashboard_useraction_following .follower .description span {
    color: #ffffff !important;
  }
  #dashboard_useraction_following .unfollow_button {
    background: #a8887d !important;
  }
  #dashboard_useraction_following .grey_note {
    color: #ffffff !important;
  }
  #dashboard_useraction_following .grey_note a {
    color: #ffffff !important;
  }
  #content_bottom {
    visibility: hidden !important;
  }
  #themes_themes .theme_header {
    color: #ffffff !important;
  }
  #themes_themes .carousel {
    background: transparent !important;
  }
  #themes_themes .previous:before {
    background: -moz-linear-gradient(right, rgba(100, 75, 66, 0) 0%, #644b42 100%) !important;
    background: -webkit-gradient(linear, right top, left top, color-stop(0%, rgba(100, 75, 66, 0)), color-stop(100%, #644b42)) !important;
    background: linear-gradient(to left, rgba(100, 75, 66, 0) 0%, #644b42 100%) !important;
  }
  #themes_themes .next:before {
    background: -moz-linear-gradient(left, rgba(100, 75, 66, 0) 0%, #644b42 100%) !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, rgba(100, 75, 66, 0)), color-stop(100%, #644b42)) !important;
    background: linear-gradient(to right, rgba(100, 75, 66, 0) 0%, #644b42 100%) !important;
  }
  .theme_container .theme_text a,
  .theme_container .theme_text .theme_by,
  .theme_list_header h2 a,
  #themes_themes .icon_search:before,
  .section_container .theme_header a,
  .section_container .theme_header div,
  .section_container .theme_header span {
    color: #ffffff !important;
  }
  .search_results_flex #container:before {
    background: #73564d !important;
  }
  .tumblelog_mask_item {
    background: #644b42 !important;
  }
  #main_help .help_search {
    background: transparent !important;
    border-width: 0 !important;
  }
  #main_about #container,
  #main_apps #container,
  #main_tips #container,
  #pages_logo_index #container,
  #pages_business_index #container,
  #main_buttons #container,
  #main_press #container,
  #main_jobs #container,
  #main_developers #container,
  #main_policy #container,
  #main_community #container,
  #main_whitehat_security_form #container {
    padding-bottom: 20px !important;
  }
  #main_about #container #content,
  #main_apps #container #content,
  #main_tips #container #content,
  #pages_logo_index #container #content,
  #pages_business_index #container #content,
  #main_buttons #container #content,
  #main_press #container #content,
  #main_jobs #container #content,
  #main_developers #container #content,
  #main_policy #container #content,
  #main_community #container #content,
  #main_whitehat_security_form #container #content {
    background: #ffffff !important;
  }
  #dashboard_promoted_directory .chrome_nav {
    border-left-color: transparent !important;
  }
  #dashboard_promoted_directory .chrome_nav li.tab a {
    border-color: transparent !important;
    color: #ffffff !important;
  }
  #dashboard_promoted_directory .chrome_nav li.tab a:hover {
    background: #826257 !important;
  }
  #dashboard_promoted_directory .chrome_nav li.tab.active .chrome {
    background: #826257 !important;
  }
  #report_tumblelog_bar {
    background: rgba(100, 75, 66, 0.85) !important;
    border-top-color: #45342e !important;
  }
  #search_form .search_form_field:before,
  #search_form .search_form_field:after {
    display: none !important;
  }
  #search_form #search_query {
    color: #ffffff !important;
  }
  .plexi .arrow {
    display: none !important;
  }
  .plexi .post_form_wrapper_inner {
    background: #644b42 !important;
  }
  .plexi #tumblelog_select span,
  .plexi .reblog_source span {
    color: #ffffff !important;
  }
  .plexi .main_content {
    border-color: #9e7a6e !important;
  }
  .plexi #post_two_manual_toolbar {
    background: #9e7a6e !important;
  }
  .plexi .popover_header {
    background: #45342e !important;
    color: #ffffff !important;
  }
  .plexi .popover_header:after {
    border-bottom-color: #261d1a !important;
  }
  .plexi .popover_inner ul li div {
    color: #ffffff !important;
  }
  .plexi .popover_inner ul li:hover div {
    color: #644b42 !important;
  }
  #report_tumblelog_bar button {
    background: -moz-linear-gradient(left, #5d463e 0%, #674d44 25%, #674d44 75%, #5d463e 100%) !important;
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, #5d463e), color-stop(25%, #674d44), color-stop(75%, #674d44), color-stop(100%, #5d463e)) !important;
    background: linear-gradient(to right, #5d463e 0%, #674d44 25%, #674d44 75%, #5d463e 100%) !important;
    border-top-color: #826257 !important;
    border-left-color: #826257 !important;
    border-bottom-color: rgba(0, 0, 0, 0) !important;
    border-right-color: rgba(0, 0, 0, 0) !important;
    -webkit-box-shadow: 2px 2px 2px 0px rgba(69, 52, 46, 0.75) !important;
    -moz-box-shadow: 2px 2px 2px 0px rgba(69, 52, 46, 0.75) !important;
    box-shadow: 2px 2px 2px 0px rgba(69, 52, 46, 0.75) !important;
  }
  .show.color {
    background: rgba(100, 75, 66, 0.85) !important;
  }
  body {
    background-attachment: fixed !important;
    background-color: #644b42 !important;
    background-repeat: repeat !important;
    background-position: 0 0 !important;
    background-image: url('data:image/png;base64,') !important;
  }
}
