css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.winpoin.com") {    
.header {
width: 100%;
background: #BC0000;
height: 150px;
padding: 15px 0px;
min-width: 950px;
}

.logo {
width: 400px;
height: 150px;
position: absolute;
margin-left: -18px;
}
    
.logo:hover {
opacity: 0.5;
}

a:hover, a:active {
color: #004499;
} 
    
#container {
width: 85%;
background: #FaFaFa;
border: 1px solid #ccc;
border-top: none;
}
    
.menu ul {
color: #fafafa;
}

.thead, .thead_prostats {
background: #BC0000;
color: #FaFaFa;
}
    
.thead {
-webkit-box-shadow: inset 0px 2px 14px 0px rgba(50, 50, 50, 0.5);
-moz-box-shadow:    inset 0px 2px 14px 0px rgba(50, 50, 50, 0.5);
box-shadow:         inset 0px 2px 14px 0px rgba(50, 50, 50, 0.5);
padding:15px;	
}

.tcat {
background: #FFEAEA;
border-bottom: 2px solid #FCEFEF;
color: #888;
font-size: 10px;
font-style: normal;
text-shadow: 0 -1px 13px #F3F3F3;
}
.tcat:hover {background: #FCEFEF}   

.trow1, .trow2 {
border-top: 1px solid #fff;
border-bottom: 1px solid #E9E9E9;
background: none;
padding:18px;
}
    
.trow_shaded {
background: #ffa;
}

.trow_selected td {
background: #FFFBD8;
}
    
.tfoot {
background: #BC0000;
color: #FFF;
padding: 12px;
text-shadow: 0 1px #303030;
}
    
hr {
	background-color: #666;
	color: #000000;
	height: 1px;
	border: 0px;
}
    
blockquote {
border: 0;
margin: 0;
background: #F0F0F0;
padding: 8px;
margin: 3px;
-webkit-box-shadow: inset 0px -1px 5px 0px rgba(50, 50, 50, 0.2);
-moz-box-shadow:    inset 0px -1px 5px 0px rgba(50, 50, 50, 0.2);
box-shadow:         inset 0px -1px 5px 0px rgba(50, 50, 50, 0.2);
}
    
blockquote cite {
	font-weight: bold;
	border-bottom: 2px dashed #DDDDDD;
	font-style: normal;
	display: block;
	margin: 3px 0;
} 
    
.post_body {
	padding: 6px;
}
    
#breadcrumb {
width: 83%;
color: #FaFaFa;
}
.smalltext {color:#888}
}
