css:
@namespace url(http://www.w3.org/1999/xhtml);

/*-----| STYLE NAME |--------------------------------------------------------------------------*/
/*---------------| by JeMhUnTeR |--------------------------------------------------------------*/

@-moz-document domain("userstyles.org") {
/*-----------------------------------------------------------------------| AD / BLOAT KILLER |-*/
    .ad, #post-install-ad {
        display: none !important;
    }
    
    /*[[social-links]]*/
    
/*------------------------------------------------------------------------| GLOBAL OVERRIDES |-*/
    * {
        border-radius: 0 !important;
        text-decoration: none !important;
        font-family: "Open Sans", sans-serif;
        font-size: 12px;
        color: #ccc;
    }
    
    input, textarea, button, select {
        background: #333;
        border: 1px solid rgba(255,255,255,0.1);
    }
    
    input[type='submit']:hover, button:hover {
        background: #444;
    }
    
/*----------------------------------------------------------------------------------| IMAGES |-*/
    .lb-cancel {
        background-image: url('http://dl.dropboxusercontent.com/s/ch9o4sfqz51v8le/loader.gif');
    }
    
/*------------------------------------------------------------------------------| FONT SIZES |-*/
    #main-header > h1 {
        font-size: 0px;
    }
    
    #category-select, #sort-select, #sort-select + select[name='sort_direction'], #per-page-select {
        font-size: 15px !important;
    }
    
    #main-header > h1 a {
        font-size: 20px;
    }
    
/*----------------------------------------------------------------------------| HEADER TEXTS |-*/
    #main-header > h1 a {
        color: #fff;
    }
    
/*----------------------------------------------------------------------------| NORMAL TEXTS |-*/
    
    
/*-----------------------------------------------------------------------------------| LINKS |-*/
    /* GENERAL LINKS */
    a, a:visited, #show-advanced-search,
    #category-select, #sort-select, #sort-select + select[name='sort_direction'], #per-page-select {
        color: #0080ff;
    }
    
    a:hover, a:visited:hover, #category-select:hover, #sort-select:hover, #show-advanced-search:hover,
    #sort-select + select[name='sort_direction']:hover, #per-page-select:hover {
        color: #80c0ff;
    }
    
    /* SPECIAL LINKS */
    
/*-----------------------------------------------------------------------------| BACKGROUNDS |-*/
    /* NONE */
    #left-sidebar nav, #search-terms, .edit-setting {
        background: none;
    }
    
    /* MAIN: #222 */
    body, body > footer p a, #search-terms:focus, .lb-outerContainer {
        background: #222;
    }
    
    /* SUB1: #333 */
    #left-sidebar, body > footer p a:hover, .style-brief, pre,
    #category-select:focus, #sort-select:focus, #screenshots,
    #sort-select + select[name='sort_direction']:focus, #per-page-select:focus,
    #advanced-search-submit button, #advanced-search-submit input {
        background: #333;
    }
    
    /* SUB2: #444 */
    #left-sidebar nav ul li a, footer#related, #search-submit, #advanced-search, #social,
    #category-select, #sort-select, #sort-select + select[name='sort_direction'], #per-page-select {
        background: #444;
    }
    
    /* SUB3: #555 */
    #search-submit:hover, #left-sidebar nav ul li a:hover, #advanced-search-submit button:hover,
    #advanced-search-submit input:hover {
        background: #555;
    }
    
    /* HIGHLIGHT: #0080ff */
    #main-header, #main-header > h1 a, .install, .install-info {
        background: #0080ff;
    }
    
    /* HIGHLIGHT SUB: #80c0ff */
    #main-header > h1 a:hover, .install:hover {
        background: #80c0ff;
    }
    
    /* 10% BLACK */
    #search-terms:hover, #category-select:hover, #sort-select:hover,
    #sort-select + select[name='sort_direction']:hover, #per-page-select:hover {
        background: rgba(0,0,0,0.1);
    }
    
/*---------------------------------------------------------------------------------| BORDERS |-*/
    /* NONE */
    #category-select, #sort-select, #sort-select + select[name='sort_direction'], #per-page-select {
        border: none;
    }
    
    /* ALL SIDES */
    #left-sidebar, #left-sidebar nav ul li a, #left-sidebar nav ul li a:hover, footer#related,
    #search-terms, #search-submit, #advanced-search, #advanced-search-submit button,
    #advanced-search-submit input, .style-brief, #social, .lb-outerContainer, .install {
        border: 1px solid;
    }
    
    /* BOTTOM ONLY */
    #main-header {
        border-bottom: 1px solid;
    }
    
/*---------------------------------------------------------------------------| BORDER COLORS |-*/
    /* TRANSPARENT */
    #search-terms {
        border-color: transparent;
    }
    
    /* 10% WHITE */
    #left-sidebar, #left-sidebar nav ul li a, #left-sidebar nav ul li a:hover, footer#related,
    .more, #advanced-search, #search-submit, #advanced-search-submit button, .install,
    #advanced-search-submit input, .style-brief, #social, #screenshots, .lb-outerContainer {
        border-color: rgba(255,255,255,0.1);
    }
    
    /* 25% WHITE */
    #search-terms:focus {
        border-color: rgba(255,255,255,0.25);
    }
    
    /* 50% BLACK */
    #main-header {
        border-color: rgba(0,0,0,0.5);
    }
    
/*-----------------------------------------------------------------------------| BOX SHADOWS |-*/
    /* 3PX 10% WHITE DROP */
    #main-header {
        box-shadow: 0 0 0 3px rgba(255,255,255,0.1);
    }
    
/*----------------------------------------------------------------------------| TEXT SHADOWS |-*/
    
    
/*-----------------------------------------------------------------------| SPACING OVERRIDES |-*/
    /* MARGINS */
    #main-header, #left-sidebar, body > footer p {
        margin: 0;
    }
    
    #advanced-search-submit button, #advanced-search-submit input {
        margin-top: 2px;
    }
    
    #advanced-search-submit {
        margin-top: 10px;
    }
    
    #content-wrapper, #main-article, #new-setting {
        margin-top: 50px;
    }
    
    #search-terms {
        margin-bottom: 2px;
    }
    
    #advanced-search, .edit-setting {
        margin-bottom: 10px;
    }
    
    #main-header > h1 a {
        margin-left: 10px;
    }
    
    /* PADDINGS */
    #main-header, #left-sidebar, #search-terms, #search-submit {
        padding: 0;
    }
    
    #social {
        padding: 5px;
    }
    
    #advanced-search, .style-brief {
        padding: 10px;
    }
    
    body > footer p a {
        padding: 0 15px;
    }
    
    /* SPACE MODIFIER */
    #left-sidebar nav ul li {
        clear: both;
    }
    
/*------------------------------------------------------------------------| REPOSITION CODES |-*/
    #main-header {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        z-index: 1000;
    }
    
    #left-sidebar {
        position: fixed;
        top: 60px;
        left: 10px;
        z-index: 1000;
    }
    
    body > footer {
        position: fixed;
        bottom: 0;
        right: 0;
    }
    
    #social {
        position: absolute;
        top: 60px;
        right: 10px;
    }
    
/*----------------------------------------------------------------------------| RESIZE CODES |-*/
    #main-header {
        height: 50px;
        line-height: 50px;
    }
    
    #main-header > h1 a {
        display: block;
        height: 100%;
        width: 200px;
        text-align: center;
    }
    
    #left-sidebar {
        width: 300px;
    }
    
    #search-terms, #search-submit, .advanced-search-active #search-terms,
    #advanced-search-submit button, #advanced-search-submit input, #show-advanced-search,
    #left-sidebar nav ul li a {
        display: block;
        width: 100%;
        height: 25px;
        line-height: 25px;
        text-align: center;
    }
    
    body > footer p a {
        display: block;
        height: 25px;
        line-height: 25px;
    }
    
    #advanced-search {
        width: calc(100% - 20px);
        text-align: center;
    }
    
    #category-select, #sort-select, #sort-select + select[name='sort_direction'], #per-page-select {
        text-align: center;
        width: 100%;
    }
    
    #social {
        text-align: center;
    }
    
/*----------------------------------------------------------------------| SPECIFIC OVERRIDES |-*/
    body > footer p a {
        border-radius: 5px 0 0 0 !important;
    }
    
    #search-submit {
        box-sizing: content-box;
        transform: translateX(2px);
    }
    
    .lb-next, .lb-prev {
        transition: none !important;
    }
}
