css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.winpoin.com") {
@viewport {
  width: device-width;
  zoom: 1;
}
.tophead {width:100%;}    
@media all and (max-width: 1024px) {
.tophead{padding:0;padding-top:10px;padding-bottom:10px}
.trow1, .trow2 {
border-top: 1px solid #fff;
border-bottom: 1px solid #E8E8E8;
background: none;
padding:5px;
}
body {padding:0;margin:0}
.header {padding:0;margin:0}    
#container {margin:0 auto;padding:0px;width:99%}
.bcwrap {
width: 70%;
float: left;
background: #FFF;
padding: 0px 10px;
margin-left:-30px;
border-bottom: 1px solid rgba(213, 213, 213, 0.29);
}  
#testimonials, .announce, .announcewrap {visibility: hidden;} 

.trow1, .trow2 {
background: none;
padding:10px;
}
    
.navi {
float: right;
margin-top: 30px;
margin-right: 0px;
}
    
.smallfooter {
margin:0;
padding:0;
padding-top:10px;
padding-bottom:10px;    
min-width: 0;
width:100%;
}
.wrap {
width: 90%;
margin: 0 auto;
text-align: left;
min-width: 0px;
}

.logo {
margin-left: -20px;
}
}
    
@media all and (max-width: 960px) {
.forums {width:100%}
.wrap {width:100%;margin:0;padding-left:30px;} 
.navi, .socnav, .headavatar {visibility: hidden;}
.logo {margin:0 auto;float:none;}    
    
}    
}
