css:
@-moz-document url-prefix("http://pr0gramm.com"){
.item-comments {
  position: fixed !important;
  top: 0;
  left: 0;
  font-size: 70%;
  color: #B4B4B4;
  
  width: 175px;
  height: 100vh;
  max-height: 100vh;
  overflow-y: auto;
  overflow-x: hidden;
}

div.item-container {
text-align: right;
}


div.item-vote {
position: absolute;
left: 320px;
top: 35px;
zoom: 55%;
}

html, body {
  zoom: 125%; }

element.style {
width: 10px;
}

#head {
zoom: 60%; 
}

div.comment-foot {
  font-size: 6pt;
  }

#stream-prev {
visibility: hidden;
}

#stream-next {
 visibility: hidden;
}

.item-container {
vertical-align: right;
}
  
 div#head {
    margin-left: 500px;
  }

div.item-comments {
  background-color: rgba(255, 255, 255, 0.2);
overflow: auto;
  
    }

.item-image {
vertical-align: bottom;

cursor: pointer;

max-height: 82%;
min-height: 82%;
}

div#head-content {
background-color: rgba(0, 0, 0, 0);
}
  
form.comment-form {
visibility: auto;
  margin-top: -50px;
 }

div.comments-head { 
visibility: hidden;
}



::-webkit-scrollbar
{
width: 12px; /* for vertical scrollbars */
height: 12px; /* for horizontal scrollbars */
}

::-webkit-scrollbar-track
{
background: rgba(0, 0, 0, 0);
}

::-webkit-scrollbar-thumb
{
background: rgba(0, 0, 0, 0);
}




div.comments { 
margin-top: -20px;
}

.tags {
    width: 100%; }

span.tag { 
font-size: 8px;
}

div.item-details {
 visibility: auto;
}

div.item-info {
  font-size: 8px;
  text-align: right;
}

.comments-large-rectangle {
  visibility: hidden;
}
 
.item-comments textarea.comment {
  resize: none;
}
 
div.comment-box > div.comment-box {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0);
    padding: 0 0 0 6px;
}
}
