css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  
url-prefix(http://www.heise.de/extras/foren),
url-prefix(http://www.heise.de/newsticker/foren),
url-prefix(http://www.heise.de/foren)
{

.forum_navi,
.forum_pfad,
.forum_aktion,
.forum_content,
form#suchfeld,
#mitte,
#logo_bereich,
#navi_login,
#navi_top,
#navi_top a, 
#navi_top ul li,
#navi_top .erste_zeile,
#navi_top .zweite_zeile,
#navigation,
#navigation li,
#navigation li a        {border:none !important; background:none !important; margin:0px !important; padding:0px !important;}

#navi_top ul li,
#navi_top .erste_zeile,
#navi_top .zweite_zeile {text-align:left !important;}

#navigation li a        {padding:0em 0.5em 0em 0em !important;}

.posting_text           {margin-top: 0.3em !important;font-size: 0.8em !important;}

.forum_pfad             {font-size: 1.0em !important;}

#navi_top a             {display:block !important;width:10em !important;}

.forum_content          {margin:5px !important;padding-top:3em !important;}

.forum_pfad             {position:absolute !important;top: 0.3em !important;}
#foren_suche            {position:absolute !important;top:-5.0em !important; left:60.5em !important;}
.forum_navi             {position:absolute !important;top: 2.5em !important;} 
.forum_aktion           {position:absolute !important;top:-1.6em !important; left:-0.1em !important;}
form#suchfeld           {position:absolute !important;top:-3.3em !important; left:69.5em !important;}
#navi_login             {position:absolute !important;top:-1.4em !important; left:62.0em !important;}
#navigation             {position:absolute !important;top:-2.5em !important;}
#navi_top               {position:absolute !important;top: 0.3em !important; left: 0.4em !important;}
#container_content      {position:absolute !important;top: 2.5em !important; left:-1.6em !important;}

.forum_navi li a:hover,
.forum_pfad li a:hover,
.forum_aktion li a:hover,
#navi_top a:hover,   
#navi_login a:hover,
#navigation a:hover     {background:#000000!important; color:#FFFFFF!important; text-decoration:none!important;}

.forum_news,
.forum_titel, 
.clear_space,
.feedback_link,
#aus,
#sitemap,
#breadcrumb,
#bannerzone,
#navi_bottom,
#logo_bereich,
#navigation li a[title="Startseite heise online"]        {display:none !important;}


