css:
@-moz-document domain("gamehubbs.com") {
/* AGENT_SHEET */
    
/* Background Colors */
html, body {
background: none repeat scroll 0% 0% rgb(22, 24, 28) !important;
}
    
.columns, label, .chosen-single, .chosen-results, .no-results, .not-click > a, .has-form, .divider, 
#SaveModal, #SignupModal, #ResetModal, #RegisterModal, #noty_topRight_layout_container > li, .comment-wrapper,
.profile, .passport-right, .content, .dataTables_empty, table {
background: none repeat scroll 0% 0% rgb(40, 42, 46) !important;
border-color: rgb(55, 59, 65) !important;
color: rgb(197, 200, 198) !important;
}
    
header, .zurb-footer-bottom, .alert-box, .title, th, .even, .odd, .current > a {
background: none repeat scroll 0% 0% rgba(24, 26, 30, 1) !important;
border: 1px solid rgba(44, 46, 50, .5) !important;
}
   
.top-bar {
background: none repeat scroll 0% 0% rgba(40, 42, 46, 0.7) !important;
box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.15) !important;
}
    
/* Text */
.chosen-single:hover, .has-tip, p, h3, td { color: rgb(197, 200, 198) !important }
    
/* Inputs */
input, textarea, button, .button, select { 
background:none repeat scroll 0% 0% rgb(40, 42, 46) !important;
border:1px solid rgb(29,31,33) !important;
color: rgb(197, 200, 198) !important;
transition: background 0.2s ease 0s;
}

input:hover, textarea:hover, button:hover, .active-result:hover, .h:hover, .has-form:hover, .not-click > a:hover, .button:hover, .chosen-single:hover, .even:hover, .odd:hover, .dataTables_paginate > ul > li:not(.unavailable) > a:hover { 
background:rgb(56,58,62)!important;transition:background .3s
}

input:focus, textarea:focus, button:focus { 
border: 1px solid rgb(129, 162, 190) !important;
}
    
/* Dropdowns */
.chosen-drop, .chosen-single { border-color: rgb(50, 50, 50) !important; border-radius: 0px !important }
.chosen-single { box-shadow: none !important; }
.chosen-search, .chosen-drop { background-color: rgb(55, 59, 65) !important }

/* Colors */
a, .blue, h2, span[style="color: rgb(125, 86, 58);"], span[style="color: rgb(0, 0, 0);"], 
span[style="position: relative; top: 10px; color: rgb(0, 0, 0);"], .noty_type_warning .noty_text, 
th, .dataTables_paginate > ul > li.unavailable > a:hover { color:#81a2be !important }
    
a:hover, span[style="color: rgb(255, 46, 52);"], span[style="position: relative; top: 10px; color: rgb(255, 46, 52);"], .noty_type_error .noty_text, .sorting_desc, .sorting_asc { color:#cc6666 !important }
span[style="color: rgb(205, 133, 63);"], span[style="position: relative; top: 10px; color: rgb(205, 133, 63);"] { color: rgba(197, 200, 198, .6) !important; }
em { color: #8abeb7 !important }
    
/* Arrows */
.arrowdown { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.arrowup { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.arrowdown:hover { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
.arrowup:hover { background: url("data:image/png;base64,") no-repeat scroll 0% 0% transparent !important }
    
/* Misc Formatting */
.active-result { border-bottom: 1px solid rgba(55, 59, 65, .8) !important }
#column-1, #column-2, #column-3 {height: 580px !important;}
em { background-color: transparent !important;}
.stat{width:240px !important;}
#column-1, #column-2 {width:30% !important;}
#column-3 {width: 40% !important;}
.highlighted {background:rgb(56,58,62)!important;transition:background .3s}
header > div:nth-child(1) > div:nth-child(1) { background: transparent !important; border: none !important;}
.has-form > a, .medium-8, div.large-6:nth-child(1), .name > label:nth-child(3), .dataTables_paginate > ul > li > a:focus, .small-block-grid-3 > li > a, #newsletter {background: transparent !important;}
hr { border-color: rgba(55, 59, 65, .8) !important }
div.display:nth-child(3) {background-color: rgba(24, 26, 30, 1) !important;border: 1px solid rgba(44, 46, 50, .5) !important;}

/* Scrollbars */
scrollbarbutton { display: none !important; }
::-webkit-scrollbar { width: 10px !important; height: 10px !important; }
::-webkit-scrollbar-thumb { border: 1px solid; }
::-webkit-scrollbar-track-piece:vertical { border-left: 0; }
::-webkit-scrollbar-track-piece:horizontal { border-top: 0; }
scrollbar, scrollcorner { -moz-appearance: none !important; width: 10px !important; height: 10px !important; }
scrollbar[orient='vertical'] { border-left: 0; }
scrollbar[orient='horizontal'] { border-top: 0; }
thumb { -moz-appearance: none !important; border: 1px solid; }
::-webkit-scrollbar-thumb:hover { background: #333; }
::-webkit-scrollbar-thumb:active { background: #111; }
::-webkit-scrollbar-track-piece, ::-webkit-scrollbar-corner, ::-webkit-scrollbar-resizer { background: #333; }
::-webkit-scrollbar-thumb { background: #1D1F21; border: #444 !important; }
scrollbar, scrollcorner { background: #1D1F21 !important; }
thumb { border-color: #444 !important; background: #1d1f21; }
thumb:active { background: #111 !important; }
thumb:hover { background: #313131; }

body {background-attachment: scroll ;}
    
    
}
