css:
.panelUI-grid .toolbarbutton-1 {
  list-style-image: none !important;
  height: 20px !important;
  width: 80% !important;
  margin: 0px !important;
  display: block !important;
}

.panelUI-grid .toolbarbutton-1 > .toolbarbutton-text,
.panelUI-grid .toolbarbutton-1 > .toolbarbutton-multiline-text {
  margin: -27px 0 0 !important;
}

#PanelUI-help,
#PanelUI-quit,
#PanelUI-help[panel-multiview-anchor="true"]{
  height: 20px !important;
  padding: 0px !important;
}

#PanelUI-fxa-status,
#PanelUI-customize {
  height: 20px !important;
  padding: 0 0 0 5px !important;
}

#PanelUI-footer {
  width: 83% !important;
}

#edit-controls,
#zoom-controls{
  width: 40% !important;
  display: inline-block !important;
  border: none !important;
  margin-left:10px !important;
}

#PanelUI-popup {
  width:250px !important;
}

toolbaritem[sdkstylewidget="true"] > toolbarbutton,
:-moz-any(#zoom-out-button, #zoom-reset-button, #zoom-in-button, #cut-button, #copy-button, #paste-button)[cui-areatype="menu-panel"],
toolbarpaletteitem[place="palette"] > :-moz-any(#zoom-out-button, #zoom-reset-button, #zoom-in-button, #cut-button, #copy-button, #paste-button) {
    padding: 0px !important;
    height: 25px !important;
}
