css:
@-moz-document domain("tweetdeck.twitter.com") {
   .is-inverted-dark {
    font-family: "HelveticaNeue","Helvetica Neue",Helvetica,Arial,Verdana,sans-serif;
   }
   * {
     border-radius: 0 !important;
   }
   .username, .account-bio{
      margin-bottom: 10px;
   }
   .prf-meta {
    border-top: 0px !important;
    background: none repeat scroll 0% 0% #FFF;
   }
   .js-profile-columns {
    border-top: 0px !important;
    background: none repeat scroll 0% 0% #FFF;
   }
   .column-header-link .icon-sliders {
      vertical-align: 0px!important; 
      font-size: 21px!important;
   }
   .column-title {
      padding-left: 10px !important;
      font-size: 18px !important;
      font-weight: 100 !important;
   }
   .column{
      margin-right: 1px !important;
      padding: 0px !important;
   }
   .tweet-avatar {
      margin-top: 8px !important;
      margin-bottom: 0px !important;
   }
   .avatar:not(.size24):not(.size73) {
      width: 24px !important;
      height: 24px !important;
   }
   .app-columns {
      padding-top: 0px !important;
      padding-left: 0px !important;
      margin-left: -1px !important;
   }
   .column-navigator {
    top: 92px !important;
    bottom: 175px !important;
   }
   .account-summary.cf{
      padding: 0px !important;
      margin-left: -15px !important;
      margin-bottom: -12px !important;
   }
   .column-header, .column-header-temp {
     height: 30px!important;
     background-color: transparent !important;
     border-bottom: none !important;
     /*border-bottom: 0px solid #3f3f3f !important;*/
   }
   .list-account .username {
     padding-top: 5px !important;
   }
      * :not(.stream-item):not(.js-column-options):not(.column-options):not(.js-inline-reply):not(.inline-reply) {
      transition: none !important;
      transition-property: none !important;
      animation: none !important;
      animation-name: none !important;
   }
   /*.app-content {transition-property: none !important;}*/
   .js-social-proof {background-color: #FFF !important;}
   .padding-tl {padding-top: 0px !important;}
   .username {margin-top: -6px !important;}
   .app-navigator {bottom: 0px !important;}
   .list-link, .list-twitter-list, .list-subtitle, .list-account, .list-listmember, .list-account, .list-listaccount, .list-subtitle, .list-filter {font-size: 1rem;}
   .tweet {padding-left: 32px !important;}
   .anim, .anim-slower, .anim-bounce-in{animation-name: none !important;}
   .item-box {padding: 3px 5px !important;}
   .js-column-scroller {width: 106.3%!important;}
   html, .tweet-body {line-height: 1.2 !important;}
   div.column-content {top: 28px!important;}
   .txt-sub-antialiased {font-size: 13px!important;}
   .sprite-drag-vertical {height: 13px!important;}
   .column-title-back .icon {top: 14px!important;}
   .column-header, .column-header-temp, .txt-ellipsis {line-height: 26px!important;}
   .compose-title {padding: 0px !important;}
   .compose-header {height: auto !important;}
   .compose-accounts {margin: 5px 5px !important;}
   .padding-hl {padding: 0px 10px !important;}
   .txt-base-smallest {font-size: 12px;}
   .column-type-icon {display:none;}
   .is-actionable {cursor: default;}
   .margin-bm {margin-bottom: 5px !important;}
   .app-title {display: none !important;}
   .account-link {color: #FFF;}
   /*.nbfc-alt {padding-left: 20px;}*/
}
