css:
@-moz-document domain("kaskus.co.id") {
    
    /* user profile */
    .user-content-wrapper {background: #f4f4f4;}
    /* .account-panel .user-menu {background: #f4f4f4;} */
    .reply-form {background:#f4f4f4;}
    
    /* nav */
    body.q ul.nav-header > li#kk-forum.current a {background: #3399ff;}
    ul#sidebar-category > li > div#list-category {background: #ededed;}
    
    /* home page */
    #mainContent {background: #f5f5f5; border-radius:4px;}
    ul#sidebar-category > li {background: #d6d6d6;}
    .h-title {color:#444444; border-bottom: #444444;}
    .hot-wrap .ht ul li a {color: #444444;}
    .hot-wrap .ht ul li {border-bottom: 1px solid #444444;}
    
    /* reply page - Forum */
    .bannerhor {background: #f4f4f4;}
    body.forum .kasad-h {background-color: #3399ff;}
    body.q {background: #999999;}
    body.q header#site-header-q {background: #d6d6d6;}
    body.forum .hfeed {background: #f0f0f0;}
    body.forum .hfeed .entry {background: #f0f0f0;}
    .hfeed .author {background: #f0f0f0;}
    .hfeed .entry-head {background-color:#3399ff;}
    body.forum #forum-listing .button {background-color:#3399ff;}
    body.forum .hfeed:after {background-color:#f0f0f0;}
    
    /* reply page - FJB */
    .hfeed .entry {background:#f0f0f0;}
    .hfeed {background:#f0f0f0;}
    
    /* pagination */
    body.forum.inner.q .pagination .active a {background:#3399ff;}
    
    /* single post */
    body {background: #999999;}
    .hfeed:after {background-color: #f4f4f4;}
    
     /* footer page */
    footer {background: #d6d6d6;}
    
    /* misc */
    a {color: #246bb2;} 
    .xkqr-entry-head { background-color: #3399ff;}
}
