css:
@-moz-document url-prefix("http://trntbl.me/"), url-prefix("http://www.trntbl.me/") {
body {
  font-family: "Courier New" !important;
}
body#playlist {
background: url('http://24.media.tumblr.com/45528d94fb26b3db8d7f6f5da25c2920/tumblr_n0umwdgj8L1skdcy7o1_1280.jpg') center no-repeat fixed !important;
background-size: 100% !important;
}
#play-pause {
      background-size: 80px !important;
}
#album .track {
    color: #ddd;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0) !important;
    border-bottom: 1px solid rgba(255, 255, 255, 0.10) !important;
    background: rgba(0, 0, 0, 0.6) !important;
}
#album .track:nth-child(odd) {
    background: rgba(0, 0, 0, 0.65) !important;
}
#album .track:hover {
  	color: #000 !important;
    background: rgba(122, 224, 52, 0.35) !important;
}
#album .track.current {
  	color: #000 !important;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 1.00) !important;
    background: #ddd !important;
    background: rgba(213, 254, 178, 0.80) !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.10) !important;
}
#turntable {
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #D5FEB2), color-stop(20%, #95C883), color-stop(40%, #75AE6C), color-stop(60%, #549355), color-stop(80%, #34783D), color-stop(100%, #145D26)) !important; /* webkit */
}
#turntable #previous {
    background: url("https://31.media.tumblr.com/cd2f92e90353fd40f8e10a09cb90edb3/tumblr_inline_n5u8y2oBYn1rks4eq.png") !important;
}
  
#turntable #panel #play-pause {
    background: url("https://31.media.tumblr.com/f9e432d53d7636c5e418c975cb27f405/tumblr_inline_n5u8xlLGLp1rks4eq.png") top !important;
    	height: 80px !important;
    background-position: 0 top !important;
}
#turntable #panel #play-pause:hover {
  	height: 80px !important;
      background-position: 0 top !important;
}
    
    #turntable #panel #play-pause:active {
    	height: 80px !important;
      background-position: 0 bottom !important;
}
  
    #turntable #panel #play-pause.pause {
    	height: 80px !important;
      background-position: 100% top !important;
}
    
      #turntable #panel #play-pause.pause:hover {
    	height: 80px !important;
        background-position: 100% top !important;
}

      #turntable #panel #play-pause.pause:active {
    	height: 80px !important;
        background-position: 100% bottom !important;
}
#turntable #next {
    background: url("https://31.media.tumblr.com/978fe8212686888db716e592bf0d0005/tumblr_inline_n5u8xh0ufJ1rks4eq.png") !important;
  }
#turntable #shuffle {
    background: url("https://31.media.tumblr.com/49d9321a5fdf5b83d06b4bc42bbedee1/tumblr_inline_n5u8ycCu1s1rks4eq.png") !important;
  }
  
    #turntable #shuffle.shuffle,
    #turntable #shuffle.shuffle:hover,
    #turntable #shuffle.shuffle:active {
      background-position: bottom center !important;
    }
#turntable #volume {
    margin-left: 5px !important;
    background: url("https://31.media.tumblr.com/10fafe9ec961d1fcdec0b70c922dfa2a/tumblr_inline_n5u8yj8O4o1rks4eq.png") !important;
  }
  
  #turntable #volume.level-25 {
    background-position: 25% center !important;
  }
    
    #turntable #volume.level-25:hover {
      background-position: 25% top !important;
    }
    
    #turntable #volume.level-25:active {
      background-position: 25% bottom !important;
    }
  
  #turntable #volume.level-50 {
    background-position: 50% center !important;
  }
  
    #turntable #volume.level-50:hover {
      background-position: 50% top !important;
    }
    
    #turntable #volume.level-50:active {
      background-position: 50% bottom !important;
    }
  
  #turntable #volume.level-75 {
    background-position: 75% center !important;
  }
  
    #turntable #volume.level-75:hover {
      background-position: 75% top !important;
    }
    
    #turntable #volume.level-75:active {
      background-position: 75% bottom !important;
    }
  
  #turntable #volume.level-100 {
    background-position: 100% center !important;
  }
  
    #turntable #volume.level-100:hover {
      background-position: 100% top !important;
    }
    
    #turntable #volume.level-100:active {
      background-position: 100% bottom !important;
    }

  #turntable #volume.level-0,
  #turntable #volume.mute {
    background-position: 0 center !important;
  }

    #turntable #volume.level-0:hover,
    #turntable #volume.mute:hover {
      background-position: 0 top !important;
    }

    #turntable #volume.level-0:active,
    #turntable #volume.mute:active {
      background-position: 0 bottom !important;
    }

  #turntable #reblog {
    margin-left: 40px !important;
    background: url("https://31.media.tumblr.com/ba4ca8ebb2c528756a925ae6691a4eb8/tumblr_inline_n5u8y8YnqC1rks4eq.png") !important;
  }
  
  #turntable #permalink {
    background: url("https://31.media.tumblr.com/f8057cc78a54ebb4a825dada20003d19/tumblr_inline_n5u8xsuS1Q1rks4eq.png") !important;
  }

  #turntable #home {
    background: url("https://31.media.tumblr.com/0acf29a9e9526b66c2b0fb4cb55e348d/tumblr_inline_n5u8x6uCpU1rks4eq.png") !important;
  }
}
