#page .region9
 {width: auto !important; min-width: auto !important;}
.wide #wrapper {padding: 0 !important;}
#wrapper {padding: 0 !important;}
#brand, #ok_main, #ok_main h2,
.ok_mypage_btnbar, .ok_lq_q_delete
 {font-size: medium !important;}

