#ok_footer #ok_footer_logo_r {float: left !important;}
#ok_footer p {display: inline !important;}
#ok_mscategoryNavi {height: auto !important; padding: 0 !important;}
#ok_mscategoryNavi>div[class^="okMscate"]
 {width: 33% !important; margin: 0 !important; padding: 0 !important;}


/*--- main contents ---*/
#ok_main {margin: 0 !important; padding: 0 !important;}
#ok_area_l , #ok_area_r {position: relative !important;}
#ok_area_l {width: 100% !important; margin: 0 -220pt 0 0 !important;}
