css:
/*
 * Kronos MCAnime: Ragnarok.
 * Creador  : Zeyth.
 * Concepto : Diseño de tono oscuro que no fatigue tanto la visión.
 * Gamma de colores : Creada para evitar fatiga visual al leer texto blanco en fondos oscuros, el contraste entre los colores reduce el cansancio visual.
 * Version  : Feb 28, 2013.
 * Cambios  : Scripts Arreglados.
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kronos.mcanime.net") {
/* Background */
body {
background-image:url('http://i.imgur.com/dggGh.jpg') !important;
background-color:#040404 !important;
background-repeat:no-repeat !important;
background-size:100% !important;
}

/* Remove Shadows Everywhere for Performance
* {
text-shadow:none !important;
box-shadow:none !important;
}
*/

/* UserBar */
#top {
background:#0B3D8D !important;
border-bottom:1px solid #1366EA !important;
height:30px !important;
margin-bottom:5px !important;
}

#top {
position:fixed !important;
width:100% !important;
z-index:2 !important;
}

#top+div:not(.online-manga-wrapper) {
padding-top:40px !important;
}

#userbar h1 {
background:url('http://i.imgur.com/3dO3lKc.png') !important;
background-position:left !important;
background-repeat:no-repeat !important;
height:30px !Important;
width:125px !Important;
margin:0 9px 0 0 !important;
padding:0 !important;
}

#userbar h1 a {
height:30px !Important;
width:125px !Important;
margin:0 !important;
padding:0 !important;
border-right:1px solid #1366EA !important;
}

#main-menu {
background:#0A367D !important;
margin:3px 0 0 0 !important;
height:24px !important;
}

#main-menu li a.selected {
background:#009900 !important;
color:#F0F0F0 !important;
border-radius: 0px !important;
}

#main-menu li a:hover {
border-radius: 0px !important;
}

#main-menu li > a {
line-height:23px !important;
height:24px !important;
font-size:12px !important;
border-radius:0 !Important;
border-right:1px solid #00296F !important;
}

#main-menu li:last-of-type > a {
border:none !Important;
}

#main-menu li.sub-menu > a.ua-arrow span, #user-actions li > a.ua-username.ua-arrow span {
background-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAECAYAAABCxiV9AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABZ0RVh0Q3JlYXRpb24gVGltZQAxMi8xMi8xMiWUR4EAAAAcdEVYdFNvZnR3YXJlAEFkb2JlIEZpcmV3b3JrcyBDUzVxteM2AAAAMklEQVQImW3KsQGAMAzEwIvZwbOygWfNEjw9pFKhW/sWLP4tXKeBqp48J9CTFHxBT8ALiqwU3c4b6o4AAAAASUVORK5CYII=') !Important;
background-repeat:no-repeat !Important;
background-position:right center !important;
}

#user-actions {
margin:0 !important;
padding:0 0 0 100px !important;
height:30px !important;
position:relative !important;
}

#user-actions li {
margin:0 !important;
padding:0 !important;
height:30px !important;
}

#user-actions > li > a#user-login {
width:62px !important;
padding:0 5px !important;
margin:2px 0 0 2px !important;
}

#user-actions > #ua-important + .ua-avatar + li {
float:right !important;
}

#user-actions > li > a.ua-register, #user-actions > li > span.sep {
display:none !Important;
}

/* Icons */
#user-actions > li > a {
border-radius: 0px !important;
height:26px !important;
width:26px !important;
padding:0 !important;
margin:2px 3px 0 3px !important;
opacity:0.9 !important;
}

#user-actions li a:hover {
opacity:1.0 !important;
}

/* Search Icon */
#user-actions li a.ua-search {
background:url('http://i.imgur.com/HCgrN69.png') !important;
}

/* Threads Icon */
#user-actions li a.ua.forums {
background:url('http://i.imgur.com/vLiBOfR.png') !important;
}

/* Notifications Icon */
#user-actions li a.ua.notifications {
background:url('http://i.imgur.com/EU7Z8hs.png') !important;
}

/* Private Messages Icon */
#user-actions li a.ua.messages {
background:url('http://i.imgur.com/6Ds4ukF.png') !important;
}

/* Friends Icon */
#user-actions li a.ua.friends {
background:url('http://i.imgur.com/b0c7tiP.png') !important;
}

/* Events Icon */
#user-actions li a.ua.important {
background:url('http://i.imgur.com/TZleAaP.png') !important;
}

/* Avatar Icon*/
#user-actions .ua-avatar{
display:none !important;
}

#user-actions .ua-avatar a {
height:26px !important;
width:24px !Important;
display:block !Important;
background:red !important;
}

#user-actions .ua-avatar img {
display:none !important;
}

/* Username Link */
#user-actions li:nth-last-child(2) {
width:100px !important;
position:absolute !important;
left:0 !important;
}

#user-actions li:nth-last-child(2) > a.ua-username {
width:auto !important;
max-width:90px !important;
text-align:left !important;
overflow:hidden !Important;
padding:0 5px 0 5px !important;
float:right !important;
}

#user-actions li:nth-last-child(2) > a.ua-username span {
width:auto !important;
max-width:90px !important;
overflow:hidden !Important;
}

/* Notification Avatar */
#user-actions .ua-block img.friend-avatar {
width:60px !important;
height:auto !Important;
}

/* Submenus */
#userbar div.ua-box {
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTIvMDkvMTLP9ekwAAAADUlEQVQImWPwVMx6BgADRgG7jwRyGQAAAABJRU5ErkJggg==') !Important;
border:1px solid #7233A3 !important;
border-radius:0 !important;
margin-top:6px !important;
color:#FFCC33 !important;
}

#userbar #main-menu div.ua-box {
margin-top:3px !Important;
}

#userbar div.ua-box a {
color:#FAFAFA !important;
}

#userbar div.ua-box a:hover {
background:#782E92 !important;
}

#userbar div.ua-box hr {
border-top:1px dashed #FF9933 !important;
position:relative !Important;
left:1px !important;
margin:0 auto 0 auto !important;
}

#userbar div.ua-box h4 {
padding-bottom:1px !Important;
border-bottom:1px dashed #FF9933 !important;
}

#userbar div.ua-box .ua-block {
padding:4px 0px !important;
}

#userbar div.ua-box .ua-block.view-all a.user-notification {
margin:0 0 -4px 0 !important;
padding:3px 0 5px 0 !important;
}

/* Container */
#wrapper {
width:100% !important;
max-width:100% !important;
min-width:980px;
overflow:hidden !important;
}

#wrapper > #top {
position:static !Important;
margin:0 !Important;
}

#container {
width:960px !important;
margin:0 auto !important;
display:table !important;
position:relative !important;
left:-1px !important;
overflow-x:hidden !Important;
}

#wrapper.w-forum #container {
width:auto !important;
}

#iconbar {
width:960px !important;
height:45px !important;
margin:0 auto 0 auto !important;
padding-left:15px !important;
}

/* Iconbar */
#iconbar .sep {
display:none !important;
}

#iconbar .icon {
margin:0 1px 0 0 !important;
padding:0 !important;
}

#iconbar .icon a {
height:45px !important;
width:38px !important;
}

#iconbar .ib-home2 a {
background:url('http://i.imgur.com/cKjxU4N.png') !important;
}

#iconbar .ib-home a {
background:url('http://i.imgur.com/cu5YWJ2.png') !important;
}

#iconbar .ib-help a {
background:url('http://i.imgur.com/IcTcDAi.png') !important;
}

#iconbar .ib-comments a {
background:url('http://i.imgur.com/ei4m7jd.png') !important;
}

#iconbar .ib-bugs a {
background:url('http://i.imgur.com/Mkl7CLn.png') !important;
}

/* Tabs / Links Top Content / Gray No Square */
#tab-menu, #content.profile-cont > #gtabs, #content.fs-cont > #gtabs, #particular_tweet #tweet-detail:not([class]) .tweet-opt, #content.fansubs > h2.group-title, #content.content-series #cs-columns > div.csc-left:before, #container > #sections > #smenu > dd, #content.enciclopedia > h2:only-of-type, #content.enciclopedia div.list h3, #topad + #container > #content[class="wide"]:not([style]) > h2.group-title, #particular_tweet:not([class]) #tweet-detail.fansub_tweet .tweet-opt, #particular_tweet #gallery h3 {
margin:0 !important;
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAK6wAACusBgosNWgAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTEvMTUvMTKJq2WlAAAADUlEQVQImWNwcHCYCQAC3gFazp0ydwAAAABJRU5ErkJggg==') !Important;
border:1px solid #333333 !important;
border-bottom:none !important;
border-right:none !important;
}

#tab-menu {
position:relative !important;
bottom:-25px !important;
margin-top:-25px !important;
min-width:655px;
}

#iconbar + #container > #tab-menu + #content.main.wide > h2.feed {
margin-top:25px !important;
}

#tab-menu ul, #content.profile-cont > #gtabs, #content.fs-cont > #gtabs {
height:25px !Important;
}

#tab-menu .selected, #content.profile-cont > #gtabs .selected, #content.fs-cont > #gtabs .selected {
background:#660066 !important;
border-radius:0 !important;
border:none !important;
}

#tab-menu li:hover, #content.profile-cont > #gtabs a:hover, #content.fs-cont > #gtabs a:hover {
background:#006600 !important;
border-radius:0 !important;
}

#tab-menu ul li a, #content.profile-cont > #gtabs a, #content.fs-cont > #gtabs a {
color:#F0F0F0 !important;
font-weight:normal !important;
font-size:12px !Important;
background:none !important;
padding:0 12px !important;
height:25px !important;
line-height:24px !important;
}

#tab-menu ul li, #content.profile-cont > #gtabs a, #content.fs-cont > #gtabs a {
background:none !important;
box-shadow:none !important;
margin:0 !important;
}

#tab-menu #user-more a {
font-size:15px !important;
line-height:22px !important;
}

#tab-menu-more {
margin-left:326px !important;
}

#tab-menu-more .sep img {
width:32px !important;
height:1px !important;
background:#660066 !important;
}

#tab-menu-more .links {
border-color:#660066 !important;
}

/* Feeds Main */
/* Black Square */
#particular_tweet + #pComment{
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAECAYAAACk7+45AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTEvMTUvMTKJq2WlAAAAIUlEQVQImWNkYGBoUFRUZGBSVFRkuH//PgPT/fv3GVBEAJXaCeuxUnJaAAAAAElFTkSuQmCC') !important;
}

#content.main.wide, #content.profile-cont, #content.profile-cont .profile-info-block, #particular_tweet, #content.fansubs[style="width:635px;"], #content.wide:not(.main), #content:not([class])[style="width:635px;"], #content.content-series #cs-columns > div.csc-left, #content.fs-cont, #content.enciclopedia {
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAECAYAAACk7+45AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTEvMTUvMTKJq2WlAAAAIUlEQVQImWNkYGBoUFRUZGBSVFRkuH//PgPT/fv3GVBEAJXaCeuxUnJaAAAAAElFTkSuQmCC') !important;
border-radius:0px !important;
border:1px solid #333333 !important;
border-top:0px !important;
border-right:0px !important;
display:table-cell !important;
margin:0 !important;
}

/* Side Bar Backgrounds */
/* Green */
#content+#sidebar .daily-releases, div#sidebar.profile-side.user-actions > div, #content.content-series #cs-columns > div.csc-right .csc-lists > .last-replies, #content.content-series #cs-columns #cs-recommendations .recommend-manga, #content.fs-cont + #sidebar.fs-side > div, #content.enciclopedia > .columns > .col.cright, #content.enciclopedia.detail .block10 .series_info, #topad + #container > #content[class="wide"]:not([style]) + #sidebar[class="fs-side"] .fansubs-series-info, #content.enciclopedia.detail[style="width:940px;"] #series_description, #particular_tweet #story-wrapper > .story-header-body , fieldset.topre {
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTIvMDYvMTKXlvlmAAAADUlEQVQImWNgS2drAAAB3gD0e3C0bgAAAABJRU5ErkJggg==') !important;
}

/* Light Green */
#content.profile-cont > h2:first-of-type, div#sidebar.profile-side.user-actions > .profile-info .profile-avatar, div#sidebar.profile-side.user-actions .sil-row .sil-title, div#sidebar.profile-side.user-actions .friends-grid-row div, #content.content-series #cs-recommendations .csc-left .r-item-txt, #content + #sidebar.fs-side .fsi-ip, #content + #sidebar.fs-side div.fansubs-series-info h4 + div:not([class]), #content.enciclopedia > .columns > .col.cright dd, #content.enciclopedia.detail .block10 .series_info .synopsis, #content.enciclopedia.detail[style="width:940px;"] #series_description .title + span, #content.enciclopedia.detail[style="width:940px;"] #series_description .title + p, #content.enciclopedia.detail[style="width:940px;"] #series_description .title + p + p, #particular_tweet #story-wrapper > .story-header-body .story-header  {
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTIvMDYvMTKXlvlmAAAADUlEQVQImWNQyFJoAAACgwEryTihUQAAAABJRU5ErkJggg==') !important;
}

#content.main.wide+#sidebar, div#sidebar.profile-side.user-actions,  #content.fansubs+#sidebar, #content + #sidebar.fs-side, #topad + #container > #content[class="wide"]:not([style]) + #sidebar[class="fs-side"] {
width:auto !important;
display:table-cell;
margin:0 !important;
padding:0 !important;
float:none !important;
vertical-align:top !important;
border:1px solid #333333 !important;
border-left:none !important;
}

#content.main.wide+#sidebar.goaway {
display:table;
}

/* Gray Square */
div#sidebar.profile-side.user-actions{
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAECAYAAACk7+45AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTEvMTUvMTKJq2WlAAAAIklEQVQImWM0MDA4w8HBwcDEwcHB8OPHDwamHz9+MKCIAACohAp8sEsO6wAAAABJRU5ErkJggg==') !important;
}

#content.main.wide+#sidebar, #content.fansubs+#sidebar, #content.content-series #cs-columns > div.csc-right, #content.fs-cont + #sidebar.fs-side, #container > #sections, #topad + #container > #content[class="wide"]:not([style]) + #sidebar[class="fs-side"] {
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAECAYAAACk7+45AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTEvMTUvMTKJq2WlAAAAIklEQVQImWO0sLA48+PHDwamHz9+MHBwcDAwcXBwMKCIAAALYw1k0kjA1AAAAABJRU5ErkJggg==') !important;
}

#content+#sidebar .daily-releases, #topad + #container > #content[class="wide"]:not([style]) + #sidebar[class="fs-side"] .fansubs-series-info, fieldset.topre {
margin:10px !important;
border-radius:0px !important;
border:1px solid #297C29 !important;
font-size:1em !important;
color:#FEFEFE !important;
font-family:verdana !important;
padding-bottom:10px !important;
}

#content+#sidebar .daily-releases legend {
display:none !important;
}

.daily-releases strong {
color:#FFFF66 !important;
font-size:1.1em !important;
font-weight:normal !important;
}

#content+#sidebar .daily-releases div {
padding:5px 3px 5px 7px !important;
}

#content+#sidebar .daily-releases a, .daily-releases.topre a {
color:#FEFEFE !important;
font-weight:normal !important;
}

#content+#sidebar .daily-releases b a, #content+#sidebar .daily-releases a.new {
font-weight:bold !important;
}

#content+#sidebar .daily-releases #view-releases {
bottom:1px !important;
background:none !important;
border:none !important;
text-transform:capitalize !important;
padding-right:0 !important;
color:#FFFF66 !important;
}

#content+#sidebar .daily-releases #view-releases:hover {
text-decoration:underline !important;
}

/* Brown */
#content.main.wide+#sidebar .side-box, #content.content-series #cs-columns > div.csc-right .csc-lists > div.last-topics, #content.content-series #cs-columns #cs-recommendations .recommend-anime, #container > #sections > #smenu > dd, #content.enciclopedia > .columns > .col.cleft {
border-radius:0 !important;
margin: 0 10px 10px 10px !important;
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTIvMDYvMTKXlvlmAAAADUlEQVQImWNIMudoAAACwgEiaAuu2QAAAABJRU5ErkJggg==') !important;
border:1px solid #7C5429 !important;
padding:3px 5px !important;
}

/* Light Brown */
#content.content-series #cs-columns #cs-recommendations .recommend-anime .r-item-txt, #container > #sections > #smenu > dd:hover, #content.enciclopedia > .columns > .col dd {
background:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABx0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzIENTNXG14zYAAAAWdEVYdENyZWF0aW9uIFRpbWUAMTIvMTAvMTKPnY8KAAAADUlEQVQImWOoizBsAAAD5wGI84An4AAAAABJRU5ErkJggg==') !important;
}

#content.main.wide+#sidebar .side-box h4 {
text-align:right !important;
font-size:12px !important;
color:#FFFF66 !important;
margin:0 !important;
border:none !important;
}

/* Sidebar Shadow
div#sidebar.profile-side.user-actions > div, #content.main.wide+#sidebar > *, #content.fansubs+#sidebar > * {
box-shadow:1px 1px 3px #111111 !important;
}
*/

#content.main.wide+#sidebar ul.arrow-list, #content.main.wide+#sidebar .cs-body ul {
list-style: none !important; 
margin:0 !important;
}

#content.main.wide+#sidebar ul.arrow-list li, #content.main.wide+#sidebar .cs-body ul li {
background:url('data:image/png;base64,') !important;
background-position:0px 3px !important;
background-repeat:no-repeat !important;
padding-left:15px !Important;
margin:0 0 5px 0 !important;
}

#content.main.wide+#sidebar ul.arrow-list a, #content.main.wide+#sidebar .cs-body ul a {
color:#F0F0F0 !important;
}

#content.main.wide+#sidebar ul.arrow-list small, #content.main.wide+#sidebar .cs-body ul small {
color:#999999 !important;
}

#content.main.wide+#sidebar ul.arrow-list small a, #content.main.wide+#sidebar .cs-body ul small a {
color:#87ADFF !important;
}

/* Feeds Main Content */
h2.feed, #content.fansubs h2.group-title {
color:#FF9900 !important;
font-family:tahoma !important;
font-size:14px !important;
margin-bottom:5px !important;
}

#content.fansubs h2.group-title {
font-size:14px !important;
}

h2.feed dfn {
border:none !important;
}

#publish-story b {
display:none !important;
}

#publish-story #story-composer, #content.fs-cont > #post-comment .reply-wrapper textarea {
background:#A69D36 !important;
border-color:#CCC363 !important;
padding:0 !important;
min-width:633px !important;
}

#publish-story label input.btns.bluebtn, #particular_tweet #pComment .add-pComment .btns , #particular_tweet + #pComment .add-pComment .btns{
border-radius:0px !important;
background:#1A1A1A !important;
border-color:#666666 !important;
}

#content #user-feeds .profile-story > div.feed_info {
padding-left:70px !important;
border-radius:0 !important;
}

/* Feeds Friends Avatar Change */
#user-feeds.main-feeds .profile-story span.story-message a img[alt][width][height] {
width:50px !important;
height:auto !important;
}

/* Feeds */
