css:
@-moz-document domain("sellegit.com") {
body {
  	padding-bottom: 60px;
}

.action-sell-multiple.fixed-top {
    display: none;
}

div.index-30-minute-auction-header {
    display: none;
}

.item.grouped-inline .item-image-frame {
    padding:  0;
}

.item.grouped-inline .item-image-frame {
    border: none;
}

.item.grouped-inline .info {
    border: none;
}

img.item-image.apply-resize-to-parent {
    width: 100% !important;
    margin-top: 0px !important;
}

img.item-image.apply-resize-to-parent {
    margin-left: 0px !important;
}

div.name {
    max-width: 50%;
    font-size: 1.5em;
    margin-left: -42px;
    font-weight: 300 !important;
    margin-top: 4px;
    height: 2em;
    padding: 5px;
    text-transform: lowercase;
}

.item.grouped-inline .current-price {
    font-size: 2.1em;
    font-weight: 300;
    margin-top: 5.5px;
  	text-align: right;
}

.four .item.grouped-inline {
    box-shadow: none;
    background: white;
}

div.item-image-wrapper {
    background: white !important;
}

div.item-image-wrapper {
    background: white !important;
    margin: 0;
    padding: 0;
}

img.item-image.apply-resize-to-parent {
    height: 100% !important;
}

div.animation.show {
    box-shadow: none;
}

.one .item.grouped-inline {
    width: 400px;
    background: white !important;
}

div.seller-avatar-wrapper.fl {
	display: none;
}

.item.grouped-inline .seller-avatar-wrapper {}
div.seller-name {
	display: none;
}

.section-title {
    border: none;
}

body {
	background-color: #ececec !important;
}
  
.section-title-day {
  	font-weight: 300 !important;
  	text-transform: lowercase;
}
  
.item.grouped-inline .auction-start-countdown, .current-price {
    font-weight: 300 !important;
}

.item-section .section-title .section-title-day-in-week {
    text-transform: lowercase;
}

.item-section .section-title .section-title-month {
    text-transform: lowercase;
}

.navbar-inner {
    opacity: 1.0;
   	box-shadow: 1px 1px 5px rgba(0,0,0,0.065) !important;
}
      
.auction-navbar .nav li>a, .auction-navbar .nav li>span, .auction-navbar .nav li>form {
    font-weight: 500;
}
         
.item-condition.used, .item-condition.new {
    border: none !important;
}

.item-single-view .item-name {
    text-transform: lowercase;
    font-weight: 300;
}
   
.item-single-view .item-widget.item-images .item-location .item-stat-cnt {
    font-weight: 300;
    text-transform: lowercase;
}
           
.item-widget-title {
    display: none;
}

.description-label {
    display: none;
}
  
.description-content {
  	margin-left: -10px;
    font-size: 14px;
    font-weight: 300;
}
    
.item-widget-comments {
    border: none;
}
      
.item-single-view .auction-start-countdown, .item-single-view .current-price, .item-single-view .sold-info{
    width: 100%;
    text-align: center;
    font-weight: 300 !important;
    margin-top: 30px;
    text-transform: lowercase;
    margin-bottom: 20px;
}
          
.item-single-view .item-main-action-wrapper {
    border: none;
    background-color: inherit;
}
             
.item-single-view .user-info-box {
    border: none;
}      
               
.item-single-view .user-info-box .user-info-name {
    font-weight: 300;
    margin-right: 5px;
}
                        
.original-price {
    text-transform: lowercase;
    font-weight: 300;
}
                          
.item-single-view .item-name {
    margin-left: 5px;
    font-color: #888;
}

.item-single-view .item-right-widgets {
    background: #fafafa;
    padding-top: 30px;
    padding-left: 30px;
    padding-right: 30px;
    padding-bottom: 25px;
}
 
.item-widget-comments {
	display: none !important;
}
      
.item-description {
    padding-left: 30px;
    padding-right: 30px;
    background: #fafafa;
}
        
.item-left-widgets {
 	background: #fafafa;
}
          
.item-top-bar {
    display: none;
}
            
.footer {
    display: none;
}
              
.howitworks .header {
    font-weight: 300;
    text-transform: lowercase;
}
                  
.howitworks .question {
    font-weight: 400;
    font-size: 1.1em;
}

.item.grouped-inline .auction-start-countdown {
    display: none;
}
                            
.modal {
    overflow: auto !important;
}
                              
.modal-header {
    border: none !important;
    background: #ffffff;
  	display: none;
}
                                
.modal-hint {
    border: none !important;
    background: #ffffff !important;
}
                                  
.user-panel-basic-info {
    border: none !important;
    font-weight: 300;                                
}                                      

.nav-wrapper {
    border-top: 0 !important;
	text-transform: lowercase;  
    border-radius: none;                              
} 
                                  
.user-panel-nav {
    border-radius: none;
    border-top: 0 !important;   
  	border: none !important;
} 

.panel-header {
    display: none;
}

.panel-content {
  	font-size: 14px !important;
    border: none !important;
}
  
.user-panel-left {
    background: white !important;
}          
                                          
.flat-btn {
  	text-transform: lowercase !important;
    font-weight: 300;
    border: none !important;
}      
                                            
.with-top-sepbar {
    border: none !important;
}                                                
                                              
input[type="text"], input[type="password"] {                                       
    border-radius: 0px !important;
}   
                                      
label {
    font-weight: 300;
    text-transform: lowercase;
}    

.notification-title {
  	font-weight: 300;
    text-transform: lowercase;
}
  
.notification-option {
    border-bottom: 0 !important;
}
    
.payment-item-labels {
    background: #ffffff !important;
    border-bottom: 0 !important;
    text-transform: lowercase !important;
    font-weight: 300;
}
      
.payment-item-label {
    font-weight: 300 !important;
   	text-transform: lowercase !important;
    font-size: 14px;
}
        
.component-title {
    border-bottom: 0 !important;
    font-weight: 300 !important;
    text-transform: lowercase !important;
}

.uv-bottom-right {
  	display: none;
}
          
.payment-component {
    border-bottom: 0 !important;
}

.field-label {
 	font-weight: 300;
    text-transform: lowercase;
}
  
.badge {
  	font-weight: 300;
  	text-transform: lowercase;
  	border-bottom: 0 !important;
}

.menu-lists {
  	display: none;
}
  
.location-wrapper, .action-sell, .user-action-wrapper {
    border-right: 0 !important;
    border-left: 0 !important;
}

.form-content-wrapper {
  	overflow: hidden !important;
}

.user-cover-content .name {
  	margin-left: 0px !important;
}
    
.stat-label {
    font-weight: 300;
    text-transform: lowercase;
}

.uv-icon {
    display: none !important;
}

.item-requirement-label {
    font-weight: 300;
}
}
