css:
@-moz-document domain("wikipedia.org") {
body {
  font-family: "Open Sans";
  background-image: url("data:image/png;base64,");
  color: white;
}
body * {
  background: none !important;
}
:link {
  color: #ffa500;
}
:visited {
  color: #7f7a75;
}
div#mw-panel {
  border-right: 1px solid #808080;
  padding: 5px 10px;
  margin: 0;
  top: 0;
}
div#mw-panel > .portal,
div#mw-panel .body {
  padding: 0 !important;
  margin: 0 !important;
}
div#mw-panel > #p-logo {
  display: none;
}
}
