css:
@-moz-document domain('trello.com'){

/*new edits*/

.list:first-child {
  opacity: 0.2 !important;
}

html, body, input, select, textarea, button {
  font: 13px "Open Sans",Arial,Helvetica,sans-serif !important;
  line-height: 17px !important;
}

.window-module>h3, span.window-header-icon.icon-lg.icon-card, span.window-module-title-icon.icon-lg.icon-checklist, span.window-module-title-icon.icon-lg.icon-activity, span.window-module-title-icon.icon-lg.icon-attachment, h3.card-detail-item-header {
    display: none !important;
}


#scrumSettingsLink, #burndownLink {
  display:none !important;
}

.board-header-btns .list-total {
  line-height: 30px !important;
  color: #fff !important;
}

.action-comment {
  background-color: rgba(53, 153, 219, 0.05) !important;
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
}

.card-label {
  text-shadow: none !important;
}

.card-label-selectable {
  color: #fff !important;
}

.board-widget-nav {
  border-radius: 0px !important;
  background: #FFFFFF !important;
}

.card-detail-item-add-button {
  background: #F8F8F8 !important;
}

.card-detail-badge {
  background-color: #F8F8F8 !important;
}

.button-link {
  background: #fafafa !important;
  background: none !important;
  border: none !important;
}
.board-header-btn-filter-indicator {
  background: none !important;
  padding-right: 30px !important;
  color: #fff !important;
  text-shadow: none !important;
  border: 1px solid rgba(255, 255, 255, 0.33) !important;
}

.board-header-btn-filter-indicator .board-header-btn-icon-close {
  background: none !important;
  color: #FFF !important;
}

.board-header-btn-filter-indicator:hover {
  background: rgba(255, 255, 255, 0.16) !important;
}  


.phenom {
  border-bottom: none !important;
  padding: 16px 0 20px 38px !important;
}

.new-comment-input {
  background-color: #fff !important;
  border: 1px solid #ddd !important;
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
}

.badge-state-complete, .badge-points {
    background-color: rgba(53, 153, 219, 1) !important;
}
.points {
    color: rgba(53, 153, 219, 1) !important;
}

.board-header-btn.board-header-btn-enabled .board-header-btn-icon.icon-star {
    color: #FFF8D5 !important;
}

.window-cover {
  background-color: #eee;
  box-shadow: none !important;
}
  

.checklist-item-state-complete .checklist-item-checkbox {
  background-color: rgba(53, 153, 219, 1) !important;
  border: 1px solid rgba(53, 153, 219, 1) !important;
  /* border-bottom-color: #ccc; */
  /* border-top-color: #999; */
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
  color: #fff !important;
}

.checklist-item-checkbox {
  background: #fff !important;
  border-radius: 0px !important;
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
  border: 1px solid rgba(53, 153, 219, 1) !important;
  /* border-bottom-color: #999; */
  /* border-top-color: #ccc; */
}
.checklist-item-checkbox.enabled:hover {
  color: rgba(53, 153, 219, 1) !important;
}

.window-header {
  margin: 40px 60px 20px 60px !important;
  padding: 0 !important;
}

.window-title-text {
font-weight: 300 !important;
font-size: 28px;
}

.window-main-col {
  margin: 0 !important;
  padding: 0 60px !important;
  width: 620px !important;
}
    
.window-module-title {
  padding: 8px 0 !important;
  margin: 30px 0 10px 0px !important;
}
    
.window {
  width: 960px !important;
}

.window-sidebar {
  padding: 40px !important;
  width: 140px !important;
}
  
.gutter {
  margin-left: 0px !important;
}    
    
.dialog-close-button {
  top: 20px !important;
  right: 20px !important;
}
    
    
    

/*old edits*/

body {
  background: #f8f8f7 !important;
  /*background-image: url("http://wallpapers.wallbase.cc/rozne/wallpaper-2984723.png")!important;*/
  font-family: "Open Sans", Arial, Helvetica, sans-serif !important;
  font-weight: 300 !important;
  background-size: cover !important;
}

h3 {font-weight:300 !important}

#header {
  background: rgba(53, 153, 219, 1) !important;
  padding: 10px !important;
}


.js-open-board  {
  background:#979798 !important;
  font-weight:300 !important;;
  
}
.js-open-board:hover  {background:rgba(53, 153, 219, 1) !important}
.thumbnail {background:#979798 !important}

.sidebar-section-header-title {text-decoration:none !important;font-weight:300 !important}
#search-text {border-radius:0px !important}


/* header buttons */
.header-btn.header-notifications, .header-btn.header-boards, 
.header-btn.header-search, .header-btn.header-login, 
.header-btn.header-signup {
  background: none !important;
}

.header-btn:hover {
  background: none !important;
}

.header-user {
  top: 10px !important;
  right: 5px !important;
  font-weight:300 !important
}

.board-header {
  height: 40px !important;
  background: rgba(53, 153, 219, 1) !important;
}

.header-btn-text {font-weight:300 !important}


.board-header-btn-text {
  text-decoration: none !important;
}

.pop-over.search-over {
  background: #FFF !important;
}

.pop-over-list li>a:hover {
  background-color: rgba(53, 153, 219, 1) !important;
}
.pop-over {
  background: #fff !important;
  border-radius: 0px !important;
  -webkit-box-shadow: 0 2px 50px rgba(0, 0, 0, 0.2) !important;
  box-shadow: 0 2px 50px rgba(0, 0, 0, 0.2) !important;
}


.board-header-btn-name  {font-weight:300 !important}

.board-widgets {
  background: #fff !important;
  border-radius: 0px !important;
  -webkit-box-shadow: 0 0 80px rgba(0, 0, 0, 0.1) !important;
}

.sidebar-hide-btn .icon-sm {
  background: #FFF !important;
  border-radius: 0px !important;
}

.text {
  text-decoration: none !important;
}

.header-member {
  background: none !important;
}

.member {
  border-radius: 20px !important;
  border: none !important;
  background: none !important;
}

.member img {
  border-radius: 0 !important;
  border: none !important;
}

.list-area {
  margin-top: 30px !important;
}

.list-header-name {
  color: rgba(53, 153, 219, 1) !important;
  font-weight: 300 !important;
  text-transform: capitalize 
}

.list {
  background: #fff !important;
}

.list-card{
  border-bottom: 1px solid rgba(0, 0, 0, 0.06) !important;
}

.list-card:hover {transition: background 0.5s ease-out !important}

.add-list {
  background: none !important;
}
.add-list:hover {
  background: #fff !important;
}

.add-list span {color: rgba(53, 153, 219, 1) !important;}  


.open-card-composer:hover {
  text-decoration: none !important;
  background: none !important;
  color: rgba(53, 153, 219, 1) !important;
}

.window-wrapper {
  background: #fff !important;
}

.window-wrapper h3 {font-weight:normal !important}

/*.window-wrapper a {text-decoration:none !important}*/


.window-overlay {
  background: rgba(53, 153, 219, 0.8) !important;
}

.js-open-move-from-header {color:rgba(53, 153, 219, 1) !important; font-weight:300 !important}

.button-link {font-weight:300}

.button-link:hover {
  background: rgba(53, 153, 219, 1) !important;
  border-color:  rgba(53, 153, 219, 1) !important;
} 

.checklist-progress-bar-current {background:rgba(53, 153, 219, 1) !important}

.confirm {background:rgba(53, 153, 219, 1) !important}
.ui-state-active {background:rgba(53, 153, 219, 1) !important}
}
