 @-moz-document domain("narutoforums.com") {
/** Removes the "Your Ad Here Links - Stolen From Golden Circle */    
* {
    border: none !important;
    }
div#pagenav_menu.vbmenu_popup + div { 
    display: none !important;
    }
body {
   	background: #1c1c29 !important;
     color: #454545 !important;
    margin: 5px 10px 10px !important;
    padding: 0 !important;
}
.page {
    background: #181822 !important;
    background-image: none !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-position: 0% 0% !important;  
    background-size: auto !important;
    background-origin: padding-box !important;
    min-width: 950px;
    max-width: 100%;
    min-height: 20%;
    max-height: 80%;
    resize: both;
    width: 950px;
    margin-left: auto;
    margin-right: auto;
}
.element {
    background-color: #1E1E1E;
}
.alt1, .alt1Active {
    background: none repeat scroll 0 0 #292929 !important;
    color: #a8a5a0 !important;
    font: 11pt Open Sans,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
}

.alt2, .alt2Active {
    background: none repeat scroll 0 0 #212224 !important;
    color: #a8a5a0 !important;
    font: 11pt Open Sans,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;

}
a:link, body_alink {
    color: #a8a5a5 !important;
}
a:visited, body_avisited {
    color: #a8a5a5 !important;
}
a:hover, a:active, body_ahover {
    color: #1a629f !important;
}

.tborder {
