css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gigazine.net") {

*{ box-sizing: border-box!important; }    
body{ margin: 0; padding: 0; font-size: 14px; }
img{ max-width: 100%!important; height: auto!important; }
    
h1, h2, h3, a, p, span, blockquote, th, td, #maincol .content ul li, #inq dt, #inq dd, ol li, .border, .date a, h3.date, #paginate, .paginate, .pagecount, .calendarBlank, .calendarMonthLinks, .calendarToday, .calendarCell{ font-family: Arial, Helvetica, sans-serif;} 

/*Color Reset*/
#pagewidth, #compagewidth, #schpagewidth, #header, #inner, #outer, #maincol, #maincol .content, #rightcol, .sidetitle, .title, h3.date, .preface, .article, b, .posted, #nextpage, #topsy-show-more-tweets, #footer, .calendarBG, .calendarHeader, .calendarMonthLinks, .calendarMonthLinks a, .calendarDayHeading, .calendarBlank, .calendarToday, .calendarCell, dl.schacdn dt, dl.acdn dt, dl.schacdn dt.active, dl.acdn dt.active{ background: transparent;}

/*Border Reset*/
#compagewidth, #schpagewidth, #header, #outer, #outer > #inner, #nextpage, #footer, dl.schacdn dt, dl.acdn dt, dl.schacdn dt.active, dl.acdn dt.active{ border: none;}

/*link*/
a:link{ color: #A00E15; }
a:visited{ color: #A00E15; }
a:hover{ color: #EB6100; background: transparent; }
a:active{ color: #EB6100; }  
.calendarMonthLinks a:hover, .calendarCell a:hover{ color: #EB6100; background: transparent; }   
    
/*header*/
#header{
    padding: 0!important;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    z-index: 1;
    background: #000;
}
#headbn{
    display: none;
}    
#logo{
    margin: 15px 0 10px 10px!important;
    height: auto!important;
    width: auto!important;
}    
#logo img{
    height: 0;
    width: 0;
    visibility: hidden;
}
#headertop{
    margin: 0 auto!important;
    padding: 0!important;
    width: 1190px!important;
}    
#logo a:after{
    content: "Gigazine";
    font-size: 24px;
    font-family: "Oswald";
    color: #FFF;
    text-transform: uppercase;
}    

/*main layout*/
body{
    background: #EEE;
    line-height: 160%;
}    

#pagewidth, #compagewidth, #schpagewidth{
    padding: 90px 0 0;
    margin: 0 auto;
    max-width: 1190px;
}
   
#leftcol{
    margin: 0;
    display: none;
    float: none;
}
    
#maincol{
    float: left;
    width: 900px;
}
#compagewidth #maincol .content{
    margin: 0 10px 60px;
    padding: 30px;
    width: 880px;
    background: #FFF;
    box-shadow: 0 1px 2px rgba(0,0,0,0.2);
    background: #FFF;
}    

#maincol .content{
    padding: 0;
}

#paginate{
    margin: 0 auto;
    padding: 10px 0;
    height: auto;
    width: 880px;
    background: #E6E6E6;
    font-size: 1.2em;
}
#nextpage{
    padding: 0;
    font-size: 100%;
    font-weight: normal;
    text-shadow: none;
}
/*pagenavi*/
#pagenavi{
    margin: 15px 0 0 0;
    padding: 0;
    border-top: 1px solid #ccc;
    width: 100%;
}
#pagenavi .next{
    margin: 0;
    padding: 15px 15px 0 0;
    border-right: 1px solid #ccc;
    width: 50%;
}    
#pagenavi .prev{
    padding: 15px 0 0 15px;
    width: 50%;
}    
#pagenavi .next b, #pagenavi .prev b{
    font-weight: normal;
}
    
/*sidebar*/
#rightcol{
    margin: 0 0 0 10px;
    padding: 1px 20px 0;
    width: 280px;
    float: left;
}
#rightcol .content{
    padding: 0;
}
dl.schacdn, dl.acdn{
    margin: 0;
    width: 100%;
}
dl.schacdn dt, dl.acdn dt{
    margin: 0 0 20px 0;
    padding: 0 0 10px;
    font-size: 1em;
    font-weight: normal;
    width: 100%;
    border-bottom: 1px solid #ccc!important;
    background: transparent;
    cursor: auto;
    line-height: 1;
}
dl.schacdn dt.active, dl.acdn dt.active, dl.schacdn dt.ov, dl.acdn dt.ov{
    margin: 0 0 20px 0;
    padding: 0 0 10px;
    font-size: 1em;
    font-weight: normal;
    width: 100%;
    background: transparent;
    cursor: auto;
}
dl.schacdn dd, dl.acdn dd{
    padding: 0 10px 0 0!important;
    display: inline!important;
}
dl.schacdn dt span, dl.acdn dt span{
    padding: 0;
}    
#cse-search-form{
    display: none;
}
#rightcol ul{
    margin: 0;
    list-style: none outside none;
}    
#rightcol ul li{
    margin: 0;
    padding: 5px 10px 5px 0;
    font-size: 1em;
}    
/*article*/
.date{
    color: #A00E15;
}
h1.title, h2.title{
    font-size: 1.5em;
    font-weight: normal;
}
.preface b{
    font-weight: normal;
}    
    
/*calendar*/
.calendarHeader{
    font-size: 1em;
    font-weight: normal;
} 
.calendarHeader th{
    font-weight: normal;
}    
.calendarBG{
    width: 100%;
}
.calendarBG, .calendarBG td, .calendarBG th{
    padding: 2px;
    border: none;
}
.calendarCell, .calendarBlank, .calendarToday{
    font-size: 1em;
    font-weight: normal;
    letter-spacing: 0;
} 
/*footer*/
#footer{
    padding: 0;
}
#footer .msnav{
    display: none;
}    
 
/*grid-content*/
#grid-content .box{
    margin: 0 10px 20px;
    padding: 30px 15px 15px;
    height: auto;
    width: 280px;
    box-shadow: 0 1px 2px rgba(0,0,0,0.2);
    background: #FFF;
}
.date{
    line-height: 1;
}
.date a{
    display: none;
}
.catab{
    padding: 5px 15px;
    color: #333;
    border: none;
    font-size: 1em;
    background: #E6E6E6;
    top: -30px;
    right: -15px;
    width: 280px;
}    
    
#grid-content .box h2.title{
    margin: 0;
    height: 3em;
}
.date{
    margin: 0 0 10px;
    font-weight: normal;
}
.boxgrid{
    margin: 0 0 10px;
    width: 250px!important;
    height: 141px!important;
}

.wideimg img{
    height: auto!important;
    width: auto!important;
    clip: rect(1px 249px 140px 1px);
}
h2.title{
    font-size: 1em;
}    
.mytitle{
    font-size: 1em;
    font-weight: normal;
    line-height: 140%;
}    

/*Font*/
.preface, .article{
    font-size: 1em;
    line-height: 160%;
}    
    
/*Ad*/
#headerbtm, #leftcol .right, #maincol .sbn, #maincol ins, #maincol iframe, #rightcol iframe, #otherad, #ufad{height: 0!important; width: 0!important; display: none;}
/*Tweets*/
#topsy-tweet-search-results{ display: none;}

/*font-face*/
@font-face {
      font-family: 'Oswald';
      font-style: normal;
      font-weight: 400;
      src: local('Oswald Regular'), local('Oswald-Regular'), url(http://themes.googleusercontent.com/static/fonts/oswald/v8/-g5pDUSRgvxvOl5u-a_WHw.woff) format('woff');
}    

/*Responsive*/    
@media (max-width: 1250px){
    #headertop,#pagewidth, #compagewidth, #schpagewidth{
        width: 900px!important;
    }
    #maincol{
        width: 900px;
    }
    #maincol .content{
        margin: 0 auto!important;
        width: 900px;
    }
    #compagewidth #maincol{
        margin-bottom: 30px;
    }
    #rightcol{
        margin: 0;
        padding: 0;
        width: 900px;
    }
    dl.schacdn, dl.acdn{
        width: 280px;
        float: left;
    }
    dl.schacdn{
        margin: 0 0 0 10px;
    }
    dl.acdn{
        margin: 0 10px 0 20px;
    }
}    

}
