css:
@-moz-document url-prefix(http://youtube.nestharion.de)
{
    body, div#wrapper
    {
        color: #222222;
        background: #F1F1F1;
        overflow: auto;
    }
    div#redirect_notice
    {
        font-size: 10px;
        background: none repeat scroll 0% 0% #FFF !important;
        box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.05);
        border: none !important;
        border-bottom: 1px solid #E8E8E8 !important;
        line-height: 13px !important;
        margin: 0px !important;
        padding: 3px !important;
    }
    div#wrapper
    {
        width: 1000px;
        margin: 10px auto 0px;
    }
    div#feedbackForm, div#shopButtons, div#logo, div#updates, div#archives, div#footer
    {
        display: none;
    }
    
    /* Title */
    div#header
    {
        height: auto;
    }
    div#current_search
    {
        font-size: 18px;
        text-align: left;
        line-height: 16px;
        position: static;
        height: auto;
    }
    div#current_search a
    {
        color: #2657AD;
        font-size: 18px;
    }
    div#current_search span img
    {
        vertical-align: middle;
    }
    
    div#content
    {
        color: #CACACA;
        font-family: arial,sans-serif;
        font-size: 13px;
        background: #222222;
        position: relative;
        height: 432px;
        margin: 10px auto 0px;
    }
    
    /* Player */
    div#ytPlayerWrapper, iframe#ytPlayer
    {
        vertical-align: top;
        float: none;
        display: inline-block;
        width: 768px !important;
        height: 432px !important;
        position: relative;
        z-index: 0;
    }
    
    /* Loader */
    div#ytPlayerWrapper div#loader
    {
        background: #222222;
        position: absolute;
        height: 115px;
        top: 50%;
        width: 100%;
        z-index: 2;
        margin: 0px auto;
        transform: translateY(-50%);
    }
    div#ytPlayerWrapper h2
    {
        padding: 45px 0px;
    }
    
    /* Search */
    div#searchForm
    {
        line-height: 18px;
        display: inline-block;
        position: absolute;
        float: none;
        width: 200px;
        padding: 0px 10px;
    }
    div#searchForm form
    {
        display: table-cell;
        vertical-align: middle;
        height: 432px;
        padding: 0px 16px;
    }
    div#searchForm input[type="text"]
    {
        color: #000000;
        font-weight: normal;
        border: 1px solid #CCC;
        box-shadow: 0px 1px 2px #EEE inset;
        
        position: relative;
        overflow: hidden;
        height: 29px;
        margin: 0px 0px 2px;
        font-size: 14px;
        line-height: 30px;
        background-color: #FFF;
        -moz-box-sizing: border-box;
        transition: border-color 0.2s ease 0s;
    }
    div#searchForm input[type="text"]:hover
    {
        border: 1px solid #B9B9B9;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1) inset;
    }
    div#searchForm input[type="text"]:focus
    {
        border: 1px solid #1C62B9;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.3) inset;
    }
    div#searchForm input[type="submit"]
    {
        display: inline-block;
        height: 28px;
        border: 1px solid transparent;
        padding: 0px 10px;
        outline: 0px none;
        font-weight: bold;
        font-size: 11px;
        text-decoration: none;
        white-space: nowrap;
        word-wrap: normal;
        line-height: normal;
        vertical-align: middle;
        cursor: pointer;
        border-radius: 2px;
        box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.05);
        
        border-color: #D3D3D3;
        background: none repeat scroll 0% 0% #F8F8F8;
        color: #333;
    }
    div#searchForm input[type="submit"]:hover
    {
        border-color: #C6C6C6;
        background: none repeat scroll 0% 0% #F0F0F0;
        box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.1);
    }
    div#searchForm input[type="checkbox"]
    {
        vertical-align: top;
    }
    div[id^="sponsor"]
    {
        height: 0px;
        display: none !important;
    }
}
