css:
@-moz-document domain("guokr.com"){ /* Delete this if you're using IE or Safari */
::-moz-selection{background: rgba(60,140,220,.4)} /* For a better selection style if not explicitly set. If you don't like, remove it.
If you don't use Firefox, remove -moz-. */

body {
    min-width: 0 !important; /* I wish this wouldn't hurt much */
}

.gh-wrap, .index-page .side, .ask-index-page .gside, .gactive,
.gh-search p, .gh-search input,
.index-page .contents-l .cont p,
.index-page .contents-r .cont p,
.gbottom, .gwrap, .gwrap-m, .gwrap-p,
.article-page, .article-page .cmt-content,
.article-rss, .all-sites, .casual, .side-article-list, .side-file,
.ask-content-page, .ask-content-page .post-detail, .ask-content-page .answer-txt,
.author-wrap .author, .name_card,
.blog_list p, .answer_list p, .news_list p, .answer-digg-wrap a,
.blog-content-page .author-title, .blog-content-page .publish-time,
.basket-lti-more a, .basket-lti-more span, .basket-iti-depict,
.ask-index-page .ask-index-help .gellipsis, .ask-index-page .ask-hot-tag,
.gpack_u, .side-groups li, .side-groups h3, .recommend-article-title, .related-article li,
.recommend-article-titles li a, .recommend-article-next {
    font-size: 100% !important;
}

html.view body[contenteditable] * {
    font-size: 14px !important;
    font-family: "segoe ui",arial,sans-serif !important;
}

.answer-digg-dw {
    line-height: 40px !important;
}

.gheader-new .gh-list {
    min-width: 6em;
}

    .gheader-new .gh-list a {
        height: 2em !important;
        line-height: 2 !important;
    }

.submit-tip-box {
    white-space: nowrap;
    width: 8em !important;
}

.name_card {
    width: 25em !important;
}

.card-focus-btn, .card-focus_complete-btn, .card-focus_cancel-btn {
    height: 1.8em !important;
    padding-left: 14px;
}

    .card-focus-btn span, .card-focus_complete-btn span, .card-focus_cancel-btn span {
        width: auto !important;
        margin: 0 !important;
    }

.gheader-wrap, .sub-nav, .gwrap, .gbottom, .index-page, .site-index-page, .side-article-list h3 {
    font-family: "segoe ui",arial,sans-serif !important;
}

.mce-content-body pre, .highlight-container pre {
    font-family: consolas,monospace !important;
}

div.highlight pre .nc, div.highlight pre .nt, div.highlight pre .k {
    font-weight: normal !important;
}

.recommend-article-next .next-left {
    margin: 8px 12px !important;
}

.recommend-article-next .next-right a, .post-recommend-title {
    height: auto !important;
}

.gactive-avatar img, .pt-pic img, .gside .gside-head, .gside .gside-head a, .gside .gside-head img {
    border-radius: 99em;
}

.index-page .side .side-user .user-info a {
    max-height: none !important;
}

/* IE is not quite compatible with media queries in user styles, see: https://connect.microsoft.com/IE/feedback/details/879228/user-styles-media-queries-only-applied-once-when-the-style-sheet-is-first-installed */
@media screen and (max-width:1030px) {
    .gheader-wrap, .gheader-new, .gh-wrap {
        height: auto !important;
    }

    .gheader-new {
        position: relative !important;
        min-width: 0 !important;
    }

        .gheader-new .gh-wrap > .gfl, .gh-notice, .gh-search, .gh-search p {
            float: none !important;
        }

            .gheader-new .gh-wrap::after, .gh-notice:after, .gheader-new .gh-nav:after, .sub-nav-wrap:after {
                display: block;
                content: "";
                clear: both;
                overflow: hidden;
            }

        .gheader-new .gh-nav {
            margin-bottom: 10px;
            float: none !important;
            position: relative;
            padding: 0 15px !important;
            margin: 0 -15px !important;
        }

            .gheader-new .gh-nav .gnarrow-up {
                border-top-color: #85c155 !important;
                border-bottom-color: transparent !important;
                top: 0;
                bottom: auto !important;
            }

    .gh-search p {
        padding-right: 42px;
    }

    .gheader-new .gh-search-txt {
        width: 100% !important;
    }

    .gheader-new .gnicon-search {
        margin-right: -42px;
    }

    .gheader-new .gh-notice {
        margin-top: 10px !important;
    }

        .gheader-new .gh-notice > li, #moreNav {
            position: static !important;
        }

    .gheader-new .gh-notice-panel .notice-content p, .gheader-new .gh-remind-panel .notice-content p {
        width: auto !important;
        margin-right: 20px !important;
    }

    .gheader-new .gh-list {
        width: 100% !important;
        border: none;
        border-bottom: 1px solid #e0e0e0;
        top: 100% !important;
    }

    .gheader-new .gh-notice-panel, .gheader-new .gh-remind-panel {
        border: none !important;
        border-bottom: 1px solid #e0e0e0 !important;
        top: 100% !important;
        width: 100% !important;
    }

    .sub-nav-wrap, .top-ads, .grow, .bottom-ads, .gbottom, .contents-l, .contents-r, .recos-article, .gwrap, .gwrap-m, .gwrap-p,
    .index-page .side .side-user .user-asks ul li p, .wrap {
        width: auto !important;
        float: none !important;
        clear: both;
    }

    .sub-nav {
        z-index: auto !important;
    }

    .sub-nav-wrap {
        height: auto !important;
    }

    .sub-nav .logged .sub-nav-link a {
        width: auto !important;
    }

    .grow .main, .gmain, .wrap .main {
        width: 100% !important;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        padding-right: 320px;
    }

    .grow .side, .gside, .wrap .side {
        width: 300px !important;
        margin-left: -300px !important;
    }

    .gwrap-p .gside {
        width: 170px !important;
        margin-left: 0 !important;
    }

    .gwrap-p .gmain {
        width: auto !important;
        float: none !important;
        padding-right: 0 !important;
        margin-top: 0 !important;
        overflow: hidden;
    }

    .index-page .profile-block {
        width: auto !important;
    }

    .user-feeds-page .gactive-combine, .recommend-best-question, .recommend-relation-question {
        width: auto !important;
        float: none !important;
    }

    /* Articles */
    .document img {
        max-width: 100% !important;
        height: auto !important;
    }

    .article-page .side .recommend-article-pic li {
        float: none !important;
        margin: 15px 0 !important;
        width: auto !important;
    }

    .recommend-footer .recommend-articles-list {
        width: auto !important;
    }

        .recommend-footer .recommend-articles-list li {
            margin: 0 15px 10px !important;
        }

    .side .recommend-article-titles li {
        width: auto !important;
    }

        .side .recommend-article-titles li a {
            height: auto !important;
            margin: 8px 0;
        }

    /* Groups */
    .titles h3, .titles h2, .titles-l {
        float: none !important;
        width: auto !important;
        margin-bottom: 10px !important;
    }

    .recos .focus {
        float: none !important;
        margin: 0 auto 1em;
    }


    .fixed-block {
        position: static !important;
        width: auto !important;
    }

    .post-recommend .item {
        width: 25%;
        margin: 5px 0 !important;
    }

        .post-recommend .item a {
            margin: 0 5px;
            display: block;
        }

    .post-recommend-title {
        width: auto !important;
    }

    .side-block-box {
        margin-right: 0 !important;
    }

    /* Answers */
    .answer-r {
        width: auto !important;
        overflow: hidden;
        float: none !important;
        clear: right;
        margin-left: 40px !important;
    }

    .answer-digg {
        width: 30px;
        margin-right: 10px;
        top: 10px !important;
    }

    .recommend-best-question .gellipsis, .recommend-relation-question .gellipsis {
        display: inline !important;
        white-space: normal !important;
        float: none !important;
    }
    /* Editor */
    .edui-editor-toolbarbox, .mce-container.mce-toolbar-grp {
        top: 0 !important;
    }

    .mce-tinymce {
        max-width: 100%;
    }

    .mce-dropzone-container {
        margin-left: 100% !important;
    }

    /* Profile */
    .gside .gside-head {
        margin: 0 auto 20px;
        overflow: hidden;
    }
}

@media screen and (max-width:900px) {
    .sub-nav-wrap h2, .sub-nav-wrap h2 a, .sub-nav-wrap .sub-nav-link, .sub-nav-wrap .sub-nav-link li {
        float: none !important;
        margin-right: 0 !important;
    }

        .sub-nav-wrap .sub-nav-link a {
            margin: 10px 0;
        }
}

@media screen and (max-width:800px) {
    .grow .main, .grow .side, .gmain, .gside, .gwrap-p .gside {
        float: none !important;
        width: auto !important;
        clear: both;
    }

    .grow .main, .gmain {
        padding-right: 0;
    }

    .gwrap-p .gmain {
        margin-top: 20px !important;
    }

    .grow .side, .gside {
        margin-left: 0 !important;
    }

    .toolBar-wrap, .recommend-article-next {
        display: none !important;
    }

    .post-recommend .item {
        float: none !important;
        width: auto;
    }

        .post-recommend .item a {
            margin: 0;
        }

    .mce-dropzone-container {
        margin-left: -1px !important;
        bottom: 100% !important;
        height: 0 !important;
        width: 100% !important;
        position: absolute !important;
        transition: .5s !important;
        border-width: 0 !important;
    }

        .mce-dropzone-container.mce-dz-visible {
            height: 200px !important;
            border: 1px solid #d9d9d9 !important;
        }

    .mce-container.mce-dropzone-container .mce-dropzone, .mce-container.mce-dropzone-container .mce-dropzone-previews {
        width: auto !important;
        height: 150px !important;
    }
}

} /* For IE/Safari delete this too */
