css:
@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain(chip.pl) {
#portal-column-two,
#portal-column-one,
#promoArea,
#polityka-cookies,
.send-news-btn,
#nextartprompt,
.portletReklama,
.ecommerce  {
display : none !important;
}
#portal-column-content {
margin-top : 0px !important;
width : 100% !important;
margin-left: -998px !important;
}
.tileItem { 
float: left !important;
height: 210px !important;
width: 475px !important;
}
.imageWide img, .imageInline img { 
max-width: 930px !important;
}
body {
background-color : #1C1B1B  !important;
color: #AFAFAF !important;
}
#content table {
background-color : #1C1B1B  !important;
}
input, textarea, .textarea-widget.required.text-field {
background: rgba(0,0,0,0.7) !important;
box-shadow: inset 0 -15px 30px rgba(0,0,0,0.4),
			0 5px 10px rgba(0,0,0,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #ffffff !important;
}
#portal-footer-bg { 
background: -o-linear-gradient(top, #14344E, #023B6F) transparent !important;
background: -moz-linear-gradient(top, #14344E, #023B6F) transparent !important;
background: -webkit-gradient(top, #14344E, #023B6F) transparent !important;
border-top: 10px solid #3B3B3B !important;
}
h3 a:link, a:visited, h2 a:link, a:visited { 
color: #72A2C9 !important;
}
dd.portletItem a:hover, .col-3 .portlet .portletItem a:hover { 
background-color: #323232 !important;
}
h2, h3, h4, h5, h6, p, #content  { 
color: #ACACAC !important;
}
#section-4col dl.portlet dt a:link, #section-4col dl.portlet dt a:visited, #section-4col dl.portlet dt a:hover, #section-4col .portlet .portletHeader a:link, #section-4col .portlet .portletHeader a:visited, #section-4col .portlet .portletHeader a:hover { 
color: #B5B2B2 !important;
}
#section-4col dd.portletItem a, #section-4col .portletItem a {
color: #72A2C9 !important;
}
#section-3col, #section-4col, #section-6col { 
background: #323232 !important;
}
#portal-logon { 
background-color: #010101 !important;
}
#portal-breadcrumbs, #portal-menusection {
background: #45484d!important; /* Old browsers */
background: -moz-linear-gradient(top, #45484d 0%, #1C1B1B 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#45484d), color-stop(100%,#1C1B1B))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #45484d 0%,#1C1B1B 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #45484d 0%,#1C1B1B 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #45484d 0%,#1C1B1B 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, #45484d 0%,#1C1B1B 100%)!important; /* W3C */
}
#portal-breadcrumbs {
margin : 0 !important;
}
#portal-globalnav, #portal-searchbox form { 
background: #B4AFAF !important;
}
#portal-breadcrumbs-inner {
background: none !important;
}
#portal-breadcrumbs-container, #portal-breadcrumbs, .comment-tile {
border: none !important;
}
#portal-breadcrumbs a, #portal-thematicnav a { 
color: #75AF00 !important;
}
blockquote { 
background: #6D6D6D !important;
color: #000000 !important;
}
.template-chip_folder_listing.portaltype-plone-site {
background-position : 0% 0px !important;
opacity: 0.9 !important;
}
body {
background-image: none !important;
}
#top-actions a {
color: #ffffff !important;
}
#portal-columns { 
background: transparent !important;
}
/*Komentarze*/
.inner-area { 
background: #313131 !important;
}
.comment-tile { 
border: 1px solid #969696 !important;
}
img {
opacity: 0.7 !important;
}
}
