@-moz-document domain("accounts.google.com"){
/* Main Styles */

body {
background: url('https://dl.dropboxusercontent.com/u/109405364/contato-smartphone.jpg') no-repeat left center !important;
background-size: cover !important;
}

.wrapper {
position: relative;
min-height: 100%;
background: url('https://dl.dropbox.com/u/12675809/patter-gdrive.png') !important;
background-repeat: repeat !important;
}

/*---- Header Bar ----*/

.google-header-bar {
height: 71px;
background: rgba(236, 28, 36, .8) !important;
border-bottom: 1px solid #ec1c24;
overflow: hidden;
zoom: 1 !important;
}

/*---- Header Bar Logo ----*/

.header .logo {
margin: 17px 0 0;
float: left;
height: 0 !important;
width: 0 !important;
background-image: url('https://dl.dropboxusercontent.com/u/109405364/header-bar-logo-meu-rancho-mail.png') !important;
background-repeat: no-repeat !important;
background-size: 120px auto !important;
padding-left: 120px !important;
padding-top: 42px !important;
}


/*---- Text Input Fields ----*/

/* input[type=email], input[type=number], input[type=password], input[type=tel], input[type=text], input[type=url] */

input[type=email], input[type=password] {
 -moz-appearance: none;
 -webkit-appearance: none;
 appearance: none;
 display: inline-block;
 height: 36px;
 padding: 0 8px;
 margin: 1px 0 5px !important;
 background: #fff;
 border-top: none !important;
 -moz-border-radius: 0 !important;
 -webkit-border-radius: 0 !important;
  font-family: "Roboto", helvetica, arial, sans-serif !important;
 font-weight: 400 !important;
 font-size: 16px !important;
 /* text-transform: uppercase !important; */
 color: #391C16 !important;
 border: 1px solid #fff !important;
 -webkit-border-radius: 0 !important;
 border-radius: 0 !important;
 -webkit-box-sizing: border-box !important;
 box-sizing: border-box !important;
 -webkit-box-shadow: none !important;
 -moz-box-shadow: none !important;
 box-shadow: none !important;
 -webkit-transition: all 0.3s ease-in-out 0s;
 -moz-transition: all 0.3s ease-in-out 0s;
 -ms-transition: all 0.3s ease-in-out 0s;
 -o-transition: all 0.3s ease-in-out 0s;
 transition: all 0.3s ease-in-out 0s;
}

input[type=email]:hover, input[type=password]:hover {
border: 1px solid #D3AC6E !important;
}

input[type=email]:focus, input[type=password]:focus {
border: 1px solid #EC1C24 !important;
}

/* Hide Big promo Heading */

.banner h1 {
 color: transparent !important;
}

/*---- Style smaller Heading Google Services Indication ----*/

.banner h2 {
 color: #ffffff !important;
 display: none !important;
}

/* Card Login Box */

.card {
background-color: #FFD733 !important;
-moz-border-radius: 2px !important;
-webkit-border-radius: 2px !important;
border-radius: 2px !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
box-shadow: none !important;
}

/* Card animation */
.signin-card {
	animation-name: slideDown;
	-webkit-animation-name: slideDown;	

	animation-duration: 1s;	
	-webkit-animation-duration: 1s;

	animation-timing-function: ease;	
	-webkit-animation-timing-function: ease;	

	visibility: visible !important;
}

.signin-card input[type=email]::-webkit-input-placeholder, .signin-card input[type=password]::-webkit-input-placeholder,  .signin-card input[type=text]::-webkit-input-placeholder {
color: #544025 !important;
}

/* Profile Image animation */

.profile-img {
	animation-name: hatch-alt;
	-webkit-animation-name: hatch-alt;
	animation-duration: 2s;	
	-webkit-animation-duration: 2s;

	animation-timing-function: ease-in-out;	
	-webkit-animation-timing-function: ease-in-out;

	transform-origin: 50% 100%;
	-ms-transform-origin: 50% 100%;
	-webkit-transform-origin: 50% 100%; 

	visibility: visible !important;	
}

.profile-img:hover{
	animation-name: hatch;
	-webkit-animation-name: hatch;
	animation-duration: 2s;	
	-webkit-animation-duration: 2s;

	animation-timing-function: ease-in-out;	
	-webkit-animation-timing-function: ease-in-out;

	transform-origin: 50% 100%;
	-ms-transform-origin: 50% 100%;
	-webkit-transform-origin: 50% 100%; 

	visibility: visible !important;	
}

/* Submit Button */

.rc-button {
 -moz-border-radius: 0px !important;
 -webkit-border-radius: 0px !important;
 border-radius: 0px !important;
 box-shadow: none !important;
 -o-transition: all 0.218s !important;
 -moz-transition: all 0.218s !important;
 -webkit-transition: all 0.218s !important;
 transition: all 0.218s !important;
 -webkit-background-clip: padding-box;
 -moz-background-clip: padding;
 background-clip: padding-box;
 -webkit-appearance: none;
}

.rc-button-submit {
 color: #EC1C24 !important;
 font-weight: 100 !important;
 text-shadow: none !important;
 background-color: transparent !important;
 background-image: none !important;
 border: 2px solid #EC1C24 !important;
 font-size: 16px !important;
 text-transform: uppercase;

}

.rc-button-submit:hover {
color: #ffffff !important;
font-weight: 100 !important;
text-shadow: none !important;
background-color: #EC1C24 !important;
background-image: none !important;
border: 2px solid #EC1C24 !important;
}

.rc-button-submit:focus {
outline:0 !important;
}

.rc-button-submit:active {
 -webkit-box-shadow: inset 0 1px 4px rgba(0, 0, 0, 0.6);
 -moz-box-shadow: inset 0 1px 4px rgba(0, 0, 0, 0.6);
 box-shadow: inset 0 1px 4px rgba(0, 0, 0, 0.6);
 border: 2px solid #EC1C24 !important;
}

/* Checkbox, Radio inputs */

input[type=checkbox], input[type=radio] {
-webkit-appearance: none;
display: inline-block;
width: 14px !important;
height: 14px !important;
background: #fff;
border: 1px solid #EC1C24 !important;
-moz-border-radius: 10px !important;
-webkit-border-radius: 10px !important;
border-radius: 10px !important;
box-shadow: none !important;
}


/* Profile name, Bubble, Help Links, Password Recovery */

.signin-card .profile-name {
