color: #EC1C24 !important;
font-weight: 600 !important;
}

.remember {
 color: #391C16 !important;
}

.bubble {
 color: #391C16 !important;
}

#link-forgot-passwd {
 color: #391C16 !important;
}

/* Create account - Sign Up Link */

#link-signup {
color: #ffffff !important;
background-color: #9B6243 !important;
font-size: 12px !important;
padding: 6px !important;
cursor: pointer !important;
text-align: center !important;
text-decoration: none !important;
text-transform: uppercase !important;
border-radius: 2px !important;
-webkit-border-radius: 2px !important;
-moz-border-radius: 2px !important;
}

#link-signup:hover {
background-color: #EC1C24 !important;
}

/* Google Account Chooser */

.switch-account a {
color: #FFD733 !important;
}
  
.accountchooser-card ol li button {
border-left: 4px solid transparent;
}
    
.accountchooser-card ol li button:focus {
border-left: 4px solid #EC1C24 !important;
}

    
.accountchooser-card ol li button:active {
border-left: 4px solid #EC1C24 !important;
}

.accountchooser-card ol li {
