css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://weibo.com/"), url-prefix("http://www.weibo.com/") {
/*木纹背景*/ 
.W_miniblog,
.S_profile .W_miniblog,
.W_miniblog_fb,
body,.S_profile {
    background-image: url('http://ww3.sinaimg.cn/large/70bde138gw1egrgjhsib8j20m80dwjt7.jpg');
    background-repeat: repeat;}
    
/*正文*/
#Box_center {background:#fff;width:830px;border-radius: 10px;}
.B_index .W_main_a{width:830px;}
.B_index .W_main
 {background-image:url("http://ww3.sinaimg.cn/large/70bde138gw1egoa85v81qj20r800k0gx.jpg");width:730px;border-radius: 10px;}    
.W_miniblog .W_main
 {background-image:url("http://ww3.sinaimg.cn/large/70bde138gw1egoa85v81qj20r800k0gx.jpg");width:830px;border-radius: 10px;} 
.B_index .W_main_c{background-color:#FaFaFa;}
.B_index .W_main.W_profile{width:600px;}   
.W_main_2r{width:222px;} 
.WB_feed .WB_screen {position: absolute;margin: -15px 0px 0px 420px;} 
    
/*隐藏项目*/
#base_scrollToTop,    
.B_index a.W_gotop,
.WB_feed .WB_arrow *,   
IMG[node-type="cover"],       
.profile_top .pf_info_right,    
.global_footer.S_bg4, 
.title_area.clearfix,    
.W_main_l,
.W_main_r, 
#pl_content_biztips, 
.key .S_textb,
div[feedtype="ad"],
.footer_adv,
.B_index .W_main_c .W_tips,
.WB_feed .popular_buss,
.ico_pai,.ico_member,.ico_club,
div[node-type="feed_list_portraitBox"]
{display:none !important;} 
div[node-type="moreList"]{display:block !important;}     
      
/*顶部导航*/ 
.W_pages_minibtn {padding-right:8px;} 
.WB_global_nav .gn_header{padding-right:0px;}
.WB_global_nav .gn_person{padding-right:200px;}    
.gn_title,.gn_logo_v2 {display:none}
.gn_title[node-type=home]{display:block} 
    
/*用户主页导航修正*/
.PRF_tab_noicon {width: 985px;border-bottom-width:0px;} 
.PRF_tab_noicon .pftb_lk {width: 115px;}   
.profile_top .pf_info_left_border {border-right-width: 0px;}     
.PRF_tab_noicon .pftb_ul {border-bottom-width: 0px;  padding-left: 1px;}    
.PRF_header .pf_info_identity {
    width: 210px; 
    padding: 0px 30px 0px 0px;
    border-left-width: 0px;}    
    
/*私信聊天调整*/
.webim_win_minD.wbim_min_friend {width:85px;}    
    
/*圆角*/    
.W_main.W_profile, 
.PRF_profile_header, 
.PRF_profile_header,  
.W_profile_bg.S_bg4,    
 .W_follow_bg.S_bg4
{border-radius: 10px;}   
  
 /*字体*/ 
.WB_feed .WB_text 
{font-size: 16px;line-height: 27px;padding: 6px 0px;}
.WB_feed .WB_media_expand .WB_name, .WB_feed .WB_media_expand .WB_text 
 {font-size: 15px;line-height: 23px;} 
.comment_list dd
{margin: 0px 0px 0px 40px;
    line-height: 24px;
    word-wrap: break-word;
    vertical-align: text-bottom;}    
body, button, input, select, textarea {
    font: 13px/1.125 微软雅黑,Helvetica,sans-serif;
}    
}
