css:
@-moz-document domain("chatvdvoem.ru")
{

div[id^="bz1"], div[id^="vk_like"], div[class^="fb-like"], div[class^="vk_subscribe_wrap"], div[id^="vk_like_"], div[id^="vk_subscribe_group_"] {display: none !important}
/* Скрытие кнопок соц. сетей */
div[id^="bz1"], div[id^="vk_like"], div[class^="fb-like"], div[class^="vk_subscribe_wrap"], div[id^="vk_like_"], div[id^="vk_subscribe_group_"] {display: none !important}
/* Скрытие кнопок соц. сетей */

.hasAsideChatMedia .logbox {right:0px}

body {background:url(http://i.imgur.com/2oeEVUY.jpg) repeat 50% 50%;;background-position:0px 0px;background-attachment:fixed}
.hasBgMedia #chatPage {background:none;}
/* Задний фон страницы */

#chatPage #header p {font-size:0px;float:none;line-height: 50px;}
#chatPage #header h1 a {color:#4A526C;opacity: 0.6}
#chatPage #header h2 {display: none !important}

#header {color:#677196} 
#header a {color:#4A526C}
#header h2 {color:#677196}

.chatmsg {border-color:#040A15}
.controlwrapper input {background:#040A15;color:#677196 !important}
.controlwrapper input[disabled] {color:#677196 !important}

#header p strong {color:#677196;background:#181C2A;}

#smartbanner.android {display:none}
#sysChat .sysSettings .themes .default a {display:none}
#sysChat .sysSettings .themes .theme1 a {display:none}
#sysChat .sysSettings .themes .theme2 a {display:none}
#sysChat .sysSettings .themes .theme3 a {display:none}
#sysChat .sysSettings .themes .theme4 a {display:none} /*  Скрытие кнопок */
#sysChat .sysSettings .themes .theme5 a {display:none} /* смены темы  */

#sysChat .sysSettings li.soundOff {background:none;padding-left:10px;}    
a {color:#677196;}/* и звука */
#sysChat .sysSettings li.soundOn {background:none;padding-left:10px;}

#messages ol li.messageTo {background:none} /* Промежуток между сообщениями, по стандарту слегка желтый */
#messages ol li.messageFrom {background:none}
#messages ol li.messageFrom em i {background:#2E3B69} /* Цвет сообщения "Некто" */
#messages ol li.messageTo em i {background:#4C5575} /* Цвет сообщения "Я" */


#page {background:#040A15} /* Обводка на главной странице "ЧатВдвоем  */

#main {background:#181C2A} /* Фон главной страницы, по середине, там где текст*/

.promo h1 {background:#39446C} /* Надпись "Общение вслепую tet-a-tet  */
.startChat a {background:#4A526C} /* Кнопка "Начать чат" */

body {color:#77819F}

#footer {color:#4A526C}
#footer a {color:#4A526C}
.startChat a {color:#677196}
.promo h1 {color:#677196}
.controlwrapper input {color:#677196} 

::-webkit-scrollbar {height: 12px!important;width: 12px!important;background:-webkit-gradient(
	linear,
	right bottom,
	right top,
	color-stop(0, #181C2A),
	color-stop(0.61, #27345E),
	color-stop(1, #181C2A)
) !important}
::-webkit-scrollbar-thumb {background-color: #161D33 !important;border-radius: 16px !important;}

small {font-size: 110%;}
#sysChat {top: 105px;}
#sysChat .inside {height: 20px;}
.logwrapper {background:-webkit-gradient(
	linear,
	right bottom,
	right top,
	color-stop(0, #181C2A),
	color-stop(0.61, #27345E),
	color-stop(1, #181C2A)
); opacity: 0.9;}
#sysChat .inside {background:#181C2A; opacity: 0.9;}
.chatmsg textarea {background: -webkit-gradient(
	linear,
	left top,
	left bottom,
	color-stop(0, #191D2B),
	color-stop(1, #161926)
)/*rgb(022, 025, 038)*/;color:#677196}
b {color:#88929F !important}
#chatPage #header p strong {opacity: 0.6;background:none}
#sysChat small {color:#677196} 
#messages ol li {border-bottom:1px dotted #292B3B}
.chatmsg textarea:focus {background:  -webkit-gradient(
	linear,
	left top,
	left bottom,
	color-stop(0, #161926),
	color-stop(1, #191D2B)
)}
#messages ol li i {color:#88929F}

::selection {background:#6F88DE;color:#fff;}
::-moz-selection {background:#6F88DE;color:#fff;}
#messages ol li.messageTo ::selection {background:#6F88DE;color:#fff;}
#messages ol li.messageTo ::-moz-selection {background:#6F88DE;color:#fff;}
#messages ol li.messageFrom ::selection {background:#6F88DE;color:#fff;}
#messages ol li.messageFrom ::-moz-selection {background:#6F88DE;color:#fff;}

.btn {color:#77819F;font:99.1%/1.2 Arial,Sans-serif;}
.btn span {background:#2A2A8F url('data:image/gif;base64,') repeat-x 0 0;border-left:1px solid #2A2A8F;border-right:1px solid #2A2A8F}
.btn span span {border-top:1px solid #2A2A8F;border-bottom:1px solid #2A2A8F}
button.pill-l span span {border-right:1px solid #2A2A8F}
button.pill-c span {border-left-color:#2A2A8F}
button.pill-c span span {border-right:1px solid #2A2A8F}
button.pill-r span {border-left-color:#2A2A8F}
.primary {color:#2A2A8F}
.btn:focus span,.btn:hover span,.btn:hover span span,.btn:focus span span {border-color:#2A2A8F !important;color:#77819F}
.btn_submit_elem {
    color:#77819F;font:99.1%/1.2 Arial,Sans-serif;
    border-top:1px solid #2A2A8F;border-bottom:1px solid #2A2A8F;
    background:#2A2A8F
url('data:image/gif;base64,') repeat-x 0 0;border-left:1px solid #2A2A8F;border-right:1px solid #2A2A8F
}
.btn_submit_elem:hover,.btn:hover span,.btn:hover span span,.btn:focus span,.btn:focus span span {cursor:pointer;border-color:#2A2A8F !important;color:#77819F;}

a img, fieldset {width:20px !important; border-radius: 50px}

.photoBtn, .photoBtn:disabled {background-image: url('data:image/png;base64,') !important;
  background-size:90% !important; border-width: thin !important; border-color:#040A15 !important}
/*  */
#cotactShowLabel {
    background-color:#040A15;
    color:#677196;
}                      
                                       /* Контакты */
.chat-media.contacts {
    background: #040A15;
    border: 5px solid #040A15;
}

#contacts_lists {
    background-color: #181C2A
}

.contact_add {
    color:#677196
}
input {
    background-color:#161926;
    border: 1px solid #040A15;
    color:#77819F
}
.cnt_label {
    color: #677196;
}
.cnt_text {
        border: 1px solid #3D435A;
}
.cnt_button {
    background: #3D435A none repeat scroll 0 0;
    border: 1px solid #3D435A;
}

#stickers_pannel {
	border-top: 1px solid #161826;
	background: #0d0e16;
}

#stickers_viewer {
    border-top: 1px solid #191D2B;
	background: #161926;
}

}
