css:
@-moz-document url-prefix("http://huaban.com/"), url-prefix("https://huaban.com/") {
:root {
    --gbshadow-0: 0 none;
    --gbshadow-1: 0 1px 3px 0 rgba(0,0,0,0.02), 0 4px 8px 0 rgba(0,0,0,0.02);
    --gbshadow-2: 0 1px 3px 0 rgba(0,0,0,0.05), 0 8px 16px 0 rgba(0,0,0,0.07);
    --gbshadow-3: 0 1px 3px 0 rgba(0,0,0,0.02), 0 16px 32px 0 rgba(0,0,0,0.07);
    --gbshadow-hover: 0 1px 3px rgba(0, 0, 0, 0.02), 0 16px 32px -4px rgba(0, 0, 0, 0.17);
    
}
html {
    background: #f5f5f5;
}
.about-pages {
    background: #fff;
}
#header {
    box-shadow: 0 1px 0 rgba(0, 0, 0, .03);
}

.pin,
.Board,
#waterfall .add-board{
    box-shadow: var(--gbshadow-1);
    border-radius: 3px;
    overflow: hidden;
    -webkit-transition: all .2s ease-out !important;
}
.pin:hover,
.Board:hover,
#waterfall .add-board:hover{
    box-shadow: var(--gbshadow-hover);
    -webkit-transform: translateY(-2px);
}
.pin-view .piece,
.pin-view .repin-info-piece .unit {
    box-shadow: var(--gbshadow-1);
}
#pin_view_layer .pin-view .piece,
#pin_view_layer .pin-view .repin-info-piece .unit {
    box-shadow: var(--gbshadow-3);
}
.pin .description{
overflow: hidden;
text-overflow:ellipsis;
white-space: nowrap;
}
.pin .attribution {
    background: #fff;
}
.pin .attribution .avt {
    border-radius: 50%;
}
.pin .actions .repin {
    border: 0 none;
}
