/*个人主页*/

#user_card {
    box-shadow: var(--gbshadow-1);
}
#user_card .maozi {
    box-shadow: var(--gbshadow-1);
}
/*对话框*/
.dialog-box {
    
    box-shadow: var(--gbshadow-3);
}
/*通知*/
#message_popup {
   box-shadow: var(--gbshadow-2); 
}
/*用户首页*/
#Profile .profile-basic {
    
    background-color: #fff;
}
#Profile .profile-stats {
    border-top: 0 none;
    border-bottom:  0 none;
}
#Profile .profile-stats a.middle {
    border-left:  0 none;
    border-right:  0 none;
}
#user_info:hover {
        box-shadow: none;
    -webkit-transform: translateY(0);
}
#Profile .profile-basic a.settings {
    background-color: #fff;
}
}
