css:
@-webkit-document domain('smartsheet.com') {
*{font-family:tahoma !important}
.clsDesktopTabInactive .clsDesktopTabContent,.clsTableHeadingText tr{
background: #5E5E5E  !important;

border: 0  !important;

}
.clsBody {
background-color: #9B9B9B;
}
.clsDesktopTabActive .clsDesktopTabContent{

background: #CACACA !important;

border: 0  !important;
}
.clsButtonDefault {

background: #CECECE !important;
}
.clsDesktopBelowTabs {

box-shadow: 0px 0px 0px rgba(0,0,0,0); 
}
.clsDesktopTab{
margin-left:5px ;
border-radius:0px
}
.clsDesktopTabCaption {

font-size: 11px !important;
}
.clsGridPrimaryColumnHeading{
font-weight:normal !important
}
TABLE{
font-size:11px;
}
td.clsTD2 {
overflow: hidden;
border-bottom-style: dashed;
border-bottom-width: 1px;
border-bottom-color: #C6C6C6;
box-sizing: border-box;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
border-right-style: dotted;
border-right-width: 1px;
border-right-color: rgba(255, 255, 255, 0);
}
td.clsTD1 {
overflow: hidden;
padding-left: 6px;
padding-top: 3px;
padding-bottom: 4px;
box-sizing: border-box;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
border-bottom-style: dashed;
border-bottom-width: 1px;
border-bottom-color: #C6C6C6;
overflow: hidden;
}
.clsDesktopActionTabs,.clsDesktopActionTab{
background-color: #DADADA !important;
}
.clsDesktopActionTabs:hover{
background-color: #eee !important;
}
.clsDesktopActionTabs{
box-shadow: 0px 0px 0px rgba(0,0,0,0)!important;
}
.clsTransparentMediumRoundCornersRight {

background: #5F5F5F!important;
}
.clsOpacityBackground {
background: #818181!important;
box-shadow: 0px 0px 0px;}
.clsHeaderUserEnteredText{
box-shadow: 0px 0px 0px rgba(0,0,0,0)!important;
}
}
