DIV.tit02,    
.bg01,    
.col_subnav ul li,    
#f-header,
.textLogin2.js_comment,  
DIV.main,
#content_box02, 
.s01,   
#spec_content
{background:none !important;}  
    
/*去除右侧边栏以及广告等*/ 
.cmtBox .textCon .pic,    
