css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.nicovideo.jp") {
#comment-post-window,
  .news-comments,
  .article-comments,
  .article-comment-count,
  .service-main-column > h1:nth-child(1),
  .service-main-column > div:nth-child(2),
  div#service-main-container header.news-article-list-header:nth-child(10),
  div#service-main-container div.news-articles-media-list:nth-child(11)
  { display:none !important; }
}

@-moz-document domain("www.nicovideo.jp") {
div#Tabs div.tabcont a.comment_count
  { display:none !important; }
}
