css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fprofile%2Fshow%") {
/* Name: Pプロフィール*/

/* 区切り線非表示 */
img[src="http://sp.pf-img-a.mbga.jp/12008305?url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fimage_sp%2Fui%2Fline_hoshi.jpg"]{
  display: none;
}

/* 領域の大きさ */
section:nth-of-type(1) {
  height: 225px;
  overflow: hidden;
}

/* 肩書き */
img[src^="http://sp.pf-img-a.mbga.jp/12008305?url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fimage_sp%2Fui%2Ftrophy%2Ftitle_charge_"] {
  margin-top: -8px;
  margin-bottom: -10px;
}

/* PRA トロフィー */
img[src^="http://sp.pf-img-a.mbga.jp/12008305?url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fimage_sp%2Fui%2Ficon_rank_kojin_win_"] {
  position: relative;
  top: -78px;
  left: -268px;
}

/* Pランク, 名前, ファン数 */
section:nth-of-type(1) table{
  margin-top: -4px;
  margin-left: 34px;
}

/* Pの属性, 成績 */
section:nth-of-type(3) table td{
  vertical-align: top !important;
}
section:nth-of-type(3) .moreButton{
  margin-top: -30px;
}
}

@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fpresent%2Frecieve") {
#Label1 table {
  margin: 0 0 2px;
  padding: 0;
}

#Label1 .infortext_nosp p {
  width: 110px;
  margin: 0;
  padding: 0;
  text-indent: -1px;
  display: block;
  float: left;
}

img[src="http://sp.pf-img-a.mbga.jp/12008305?url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fimage_sp%2Fui%2Fline_hanyo_ten.jpg"] {
  margin: 0 0 3px;
}

}

@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fauction%2Fsearch_top") {
.grayButton80{
  width: 140px !important;
  float: left;
}
  
.idolStatus > .data_list:nth-of-type(3){
  width: 50px;
  float: left;
}
  
.idolStatus > .data_list:nth-of-type(3):hover{
  width: 300px;
}
}

@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fauction%2Fhistory") {
a[href*="Fhistory_detail"] div{
  text-align: left;
  float: left;
  width: 180px !important;
  padding: 0 !important;
  overflow: hidden;
}

a[href*="Fhistory_detail"] + br{
  display: none;
}

.t-Rgt img{
  display: none;
}

.m-Btm5 {
  margin-bottom: 8px !important;
}
}

@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fgacha%2Fresult") {
.idolStatus .statusTable,
.idolStatus .data_list {
  display: none !important;
}

}

@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fcard_str") {
.selectArea{
  display: none !important;
}

.selectArea + img{
  display: none !important;
}
}

@-moz-document url-prefix("http://sp.pf.mbga.jp/12008305/?guid=ON&url=http%3A%2F%2F125.6.169.35%2Fidolmaster%2Fcard_storage") {
  
.idolStatus > #Label1 > .line02 {
  width: 110px;
  float: left;
}
  
.idolStatus > #Label1 > .line02:hover {
  width: 300px;
}

.idolStatus > #Label1 > .line02 + div[style="text-align:right;"] {
  width: 180px !important;
  text-align: left !important;
  float: left;
}

.idolStatus:after {
  content: "";
  display: block;
  clear: both;
}

}
