css:
@-moz-document regexp("(.+\\.|)freebbs\\.tw/(index\\.php.*|)") {
/* 首页上方公告框 和 风格选择区 */
div.maintable > div.maintable > div > div.spaceborder,select {
  display:none;
}

.spaceborder .header {
  background: DodgerBlue;
  color: white;
}
.spaceborder .header a > font {
  color: white;
}
iframe{margin-top: 0.5em;}
div.maintable > div.maintable > div > div > table > tbody > tr > td[align=right] > * {display:none;}
div.maintable > div.maintable > div > div > table > tbody > tr > td[align=right] > *:first-of-type {
  display:block;
}
script{display:none !important;}
.footer_color{display:none;}
br,div.maintable > div.maintable > div > div:nth-child(8),#popLayer{display:none !important;}
div.maintable > div.maintable > div > div:nth-child(13){height: 1em;}
div.maintable > div.maintable > div{padding-bottom: 1em !important;}
.smalltxt{display:none;}
.mainborder{display:none;}
}

@-moz-document regexp("(.+\\.|)freebbs\\.tw/FREECHAT4/.+") {
/* 聊天框 */
#MessagesContainer {
  overflow:auto !important;
}
#MessagesContainer > div {
  display:none !important;
}
#Messages {
  height:initial !important;
}
.MessageRow {
  display:none;
}
.MessageRow:nth-last-of-type(0),.MessageRow:nth-last-of-type(1),.MessageRow:nth-last-of-type(2),.MessageRow:nth-last-of-type(3),.MessageRow:nth-last-of-type(4),.MessageRow:nth-last-of-type(5) {
  display:initial;
}
.MessageRow > div {
  background: dodgerblue;
  margin: 0.25em;
  padding: 0.25em;
}
.SenderName a {
  color:white !important;
  font-size: 15px;
}
.SenderName a:after {
  content: " :"
}
.MsgCreatedAT{
  display: none; 
}
.Msg {
  color: white !important;
  padding: 0.25em 0.5em 0.25em 1em;
}
div#Panel {
  display: block !important;
}
.SubmitImg {
  background: #2672ec !important;
  color: white;
  height: 2.142em !important;
  min-width: 6em;
  padding: 3px 12px 5px;
}
.SubmitImg:hover {background: #5a94f1 !important;}
.SubmitImg:active {background: #212121 !important;}
#message {
  color: black !important;
  font-size: 1em;
  height: 24px;
  margin-left: -2px;
  width: 180px !important;
}
#Panel > img{display:none !important;}
#Mask,#jqi{display:none !important;}
}

@-moz-document regexp("(.+\\.|)freebbs\\.tw.*") {
body {
  font-family : "Microsoft Yahei UI","Microsoft Yahei",Verdana,Simsun,"Segoe UI Semibold","Segoe UI Web Semibold","Segoe UI Web Regular","Segoe UI","Segoe UI Symbol","HelveticaNeue-Medium","Helvetica Neue",Arial,sans-serif;;
  font-size: 16px !important;
  font-weight: 100;
}
}
