@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("youtube.com"),domain("www.youtube.com") {
/*strona główna*/
.video-masthead,
#premium-yva,
#header #ad_creative_1,
#alerts {
display: none !important;
}
#yt-masthead-container, #masthead-container {
background: rgb(215,226,237)!important; /* Old browsers */
background: -moz-linear-gradient(top, rgba(215,226,237,1) 0%, rgba(186,208,228,1) 17%, rgba(129,174,211,1) 50%, rgba(96,156,208,1) 51%, rgba(104,175,222,1) 59%, rgba(111,199,243,1) 71%, rgba(96,163,208,1) 84%, rgba(119,141,167,1) 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(215,226,237,1)), color-stop(17%,rgba(186,208,228,1)), color-stop(50%,rgba(129,174,211,1)), color-stop(51%,rgba(96,156,208,1)), color-stop(59%,rgba(104,175,222,1)), color-stop(71%,rgba(111,199,243,1)), color-stop(84%,rgba(96,163,208,1)), color-stop(100%,rgba(119,141,167,1)))!important; /* Chrome,Safari4+ */
