background: -o-linear-gradient(top, rgba(215,226,237,1) 0%,rgba(186,208,228,1) 17%,rgba(129,174,211,1) 50%,rgba(96,156,208,1) 51%,rgba(104,175,222,1) 59%,rgba(111,199,243,1) 71%,rgba(96,163,208,1) 84%,rgba(119,141,167,1) 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, rgba(215,226,237,1) 0%,rgba(186,208,228,1) 17%,rgba(129,174,211,1) 50%,rgba(96,156,208,1) 51%,rgba(104,175,222,1) 59%,rgba(111,199,243,1) 71%,rgba(96,163,208,1) 84%,rgba(119,141,167,1) 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, rgba(215,226,237,1) 0%,rgba(186,208,228,1) 17%,rgba(129,174,211,1) 50%,rgba(96,156,208,1) 51%,rgba(104,175,222,1) 59%,rgba(111,199,243,1) 71%,rgba(96,163,208,1) 84%,rgba(119,141,167,1) 100%)!important; /* W3C */
}
.lohp-video-link { 
color: #FCFCFC !important;
}
.lohp-video-metadata { 
color: #EEEEEE !important;
}
.lohp-video-metadata .view-count { 
color: #FFFFFF !important;
font-weight : bold !important;
}
.shelf-description { 
color: #FFFFFF !important;
background-color: #48B2D9 !important;
}
#browse-items-primary .display-message, .feed-message, .no-content-message, .no-recent-activity,
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item , #watch7-secondary-actions .yt-uix-button, span, p, div, h3, .yt-default .yt-notes, p.yt-notes,
#watch-description-extras .title, .branded-page-module-title, .pl-header .pl-header-title, li { 
color: #FFFFFF !important;
}
a {
color: #FFFFFF !important;
text-decoration: underline !important;
}
a:hover, .title:hover {
color: #90b2cd !important;
}
#footer-links-primary a:hover, #footer-links-secondary a:hover , .footer-secondary a:hover{
color: #25637c !important;
text-shadow: none !important;
}
.footer-secondary .footer-secondary-info a {
text-shadow: none !important;
}
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
 .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
 color: #FFFFFF !important;
 font-weight : 700 !important;
 }
.yt-ui-ellipsis { 
background-color: #4794B4 !important;
}
.yt-lockup-deemphasized-text, .yt-lockup a.yt-lockup-deemphasized-text { 
color: #D9D9D9 !important;
}
.guide-flyout, .browse-list-item-container.feed-item-container.branded-page-box, .compact-shelf.branded-page-box,
 .channel-header .primary-header-contents, .branded-page-v2-subnav-container, #video-page-content, #playlist-page-content, #channel-page-content,
 #channels-browse-content-grid.grid-lockups-container, .branded-page-v2-body, .site-center-aligned .yt-card, 
 #footer .yt-picker, .DJa, .yt-uix-expander-ellipsis, #watch7-sidebar, .watch-wide #watch7-sidebar, .watch-wide #watch7-preview {
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}

/*borders*/
div, td {
border-color: #7db9e8 !important;
}

/*background*/
body,  #ticker, #page, #body-container, .footer-container , .guide-pinned.show-guide .guide-pinning-enabled #page, .guide-pinned.show-guide .guide-pinning-enabled #appbar-content, body #footer-container, #content-container, #body-containe { 
background: #7db9e8 !important;
}
#appbar-guide-menu, .guide-flyout, .browse-list-item-container.feed-item-container.branded-page-box, .compact-shelf.branded-page-box,
 .channel-header .primary-header-contents, .branded-page-v2-subnav-container, #video-page-content, #playlist-page-content, #channel-page-content,
 #channels-browse-content-grid.grid-lockups-container, .branded-page-v2-body, .site-center-aligned .yt-card, #footer .yt-picker, .DJa, .yt-uix-expander-ellipsis { 
background: #4794B4 !important;
}

/*progress*/
#progress { 
background: rgba(96,156,208,0.6) !important;
padding-top: 48px !important;
}
#progress dd, #progress dt {
display: none !important;
}

/*małe przyciski*/
.guide-item.guide-item-selected, .shelves-play , .yt-uix-button-default, #watch-description-expand button, .yt-uix-expander-head button,
.compact-shelf .yt-uix-button-shelf-slider-pager, .yt-uix-button-dark { 
background: #8bb4d4 !important;
border: 1px solid #C6C6C6 !important;
box-shadow: 0px 0px 0px 2px rgba(27, 127, 204, 0.400) !important;
-webkit-border-radius: 15px !important;
-moz-border-radius: 15px !important;
border-radius: 15px !important;
text-decoration: none !important;
}
.guide-item.guide-item-selected:hover, .shelves-play:hover , .yt-uix-button-default:hover, #watch-description-expand button:hover,
 .yt-uix-expander-head button:hover, .compact-shelf .yt-uix-button-shelf-slider-pager:hover{ 
background: #a2c8e5 !important;
}
.yt-uix-button-menu  {
background: #8bb4d4 !important;
}
#appbar-guide-button, #upload-btn, .yt-uix-button-primary, a.yt-uix-button.yt-uix-button-epic-nav-item, #masthead-utility a.yt-uix-button {
border: none !important;
background: none !important;
box-shadow: none !important;
font-size: 12px !important;
text-decoration: none !important;
}
#appbar-guide-button:hover, #upload-btn:hover, .yt-uix-button-primary:hover, a.yt-uix-button.yt-uix-button-epic-nav-item:hover, #masthead-utility a.yt-uix-button:hover {
border-bottom: 3px solid #6282c1 !important;
-webkit-border-bottom-right-radius: 15px!important;
-webkit-border-bottom-left-radius: 15px!important;
-moz-border-radius-bottomright: 15px!important;
-moz-border-radius-bottomleft: 15px!important;
border-bottom-right-radius: 15px!important;
border-bottom-left-radius: 15px!important;
text-decoration: none !important;
}
#yt-masthead #search-btn, #masthead #search-btn {
border: none !important;
background: none !important;
box-shadow: none !important;
opacity: 0.3 !important;
}
#yt-masthead #search-btn:hover, #masthead #search-btn:hover {
border: none !important;
background: none !important;
box-shadow: none !important;
opacity: 1 !important;
}
.yt-uix-button-menu .yt-uix-button-menu-item:hover {
background: #4794B4 !important;
}

/*paski menu, przyciski w kategoriach*/
a.yt-uix-button-epic-nav-item.partially-selected:hover,
#watch7-secondary-actions .yt-uix-button.yt-uix-button-toggled,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading,
#watch7-secondary-actions .yt-uix-button:hover,
#share-panel-buttons .yt-uix-button:hover, 
#share-panel-buttons .yt-uix-button:active, 
#share-panel-buttons .yt-uix-button.yt-uix-button-active, 
#share-panel-buttons .yt-uix-button.yt-uix-button-toggled { 
border-bottom: 3px solid #6282c1 !important;
-webkit-border-bottom-right-radius: 15px!important;
-webkit-border-bottom-left-radius: 15px!important;
-moz-border-radius-bottomright: 15px!important;
-moz-border-radius-bottomleft: 15px!important;
border-bottom-right-radius: 15px!important;
border-bottom-left-radius: 15px!important;
}
a.yt-uix-button.yt-uix-button-epic-nav-item:hover, #watch7-secondary-actions .yt-uix-button:hover{
border-bottom: 3px solid #6282c1 !important;
}
#c4-header-bg-container.has-custom-banner, .branded-page-v2-top-row #channel-subheader, #appbar-nav { 
background-color: #48B2D9 !important;
}

/*kategorie*/
.branded-page-related-channels-item .yt-lockup .yt-lockup-meta, .yt-lockup-mini .yt-lockup-title  {
background: #4794B4 !important;
margin-top: 10px !important;
}

/*lewe menu*/
.guide-item:hover {
background: #90b2cd !important;
}

/*czcionki*/
.channel-header .branded-page-header-title .branded-page-header-title-link { 
font-size: 23px !important;
}
.branded-page-module-title {
font-size: 18px !important;
}

/*tooltips*/
.yt-uix-range-tooltip-tip-content, .yt-uix-tooltip-tip-content {
background: #7db9e8 !important;
}

/*subskrybuj*/
.yt-uix-button-subscribe-unbranded,  .yt-uix-button-subscribe-branded, .yt-uix-button-subscribe-unbranded[disabled] { 
background: #8bb4d4 !important;
border: 1px solid #C6C6C6 !important;
box-shadow: 0px 0px 0px 2px rgba(27, 127, 204, 0.400) !important;
-webkit-border-radius: 15px !important;
-moz-border-radius: 15px !important;
border-radius: 15px !important;
}
.yt-uix-button-subscribe-unbranded[disabled]:active, .yt-uix-button-subscribe-unbranded[disabled]:focus {
background: -moz-linear-gradient(top, #dce6f0 0%, #c3d6e7 17%, #8fb7d7 50%, #71a6d5 51%, #78b8e2 59%, #71add5 84%, #4584cd 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#dce6f0), color-stop(17%,#c3d6e7), color-stop(50%,#8fb7d7), color-stop(51%,#71a6d5), color-stop(59%,#78b8e2), color-stop(84%,#71add5), color-stop(100%,#4584cd))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #dce6f0 0%,#c3d6e7 17%,#8fb7d7 50%,#71a6d5 51%,#78b8e2 59%,#71add5 84%,#4584cd 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #dce6f0 0%,#c3d6e7 17%,#8fb7d7 50%,#71a6d5 51%,#78b8e2 59%,#71add5 84%,#4584cd 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #dce6f0 0%,#c3d6e7 17%,#8fb7d7 50%,#71a6d5 51%,#78b8e2 59%,#71add5 84%,#4584cd 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, #dce6f0 0%,#c3d6e7 17%,#8fb7d7 50%,#71a6d5 51%,#78b8e2 59%,#71add5 84%,#4584cd 100%)!important; /* W3C */
}
.yt-uix-button-subscribe-unbranded:hover,  .yt-uix-button-subscribe-branded:hover { 
background: #a2c8e5 !important;
}
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-branded-vertical, .yt-subscription-button-subscriber-count-unbranded-horizontal, .yt-subscription-button-subscriber-count-unbranded-vertical {
color: #000000 !important;
}

/*wideo*/
#watch7-headline, .action-panel-content, #watch7-user-header, #watch-discussion, #watch7-main ,
#watch7-sidebar, #watch7-content, .yt-ui-menu-content {
background: #4794B4 !important;
}
#watch7-action-panels #watch7-action-panel-footer { 
background-color: #8FBBC5 !important;
}
.video-extras-sparkbar-likes { 
background: #17329F !important;
}
#watch7-action-buttons {
padding: 10px 18px !important;
background-color: #41a9d0 !important;
border-bottom: 10px solid #7db9e8 !important;
border-top: 10px solid #7db9e8 !important;
border-right: 1px solid #7db9e8 !important;
}
.action-panel-content {
border-right: 1px solid #7db9e8 !important;
}
#watch7-action-panel-footer {
display: none !important;
}

/*ExtendTube*/
.ext-lyrics .yt-uix-expander-head, .ext-actions-container, .yt-uix-button-panel, .ext-lyrics-body-info {
background: #4794B4 !important;
}

/*theater mode*/
#theater-background { 
background: #7db9e8!important; 
}

/*playlista*/
#watch-appbar-playlist .playlist-videos-list, #watch-appbar-playlist .playlist-videos-list .yt-ui-ellipsis  {
background: #4794B4 !important;
}
#watch-appbar-playlist .playlist-videos-list > li:hover, #watch-appbar-playlist .playlist-videos-list > li.currently-playing, #watch-appbar-playlist .playlist-videos-list > li:hover .yt-ui-ellipsis, #watch-appbar-playlist .playlist-videos-list > li.currently-playing .yt-ui-ellipsis {
background-color: #008BB5 !important;
}
#watch-appbar-playlist { 
background: #41a9d0 !important;
}
#watch-appbar-playlist .playlist-video h4 { 
color: #ffffff  !important;
}
#watch7-sidebar-contents {
background: #4794B4 !important;
border-top: 1px solid #7db9e8 !important;
margin-top: 0 !important;
padding-top: 10px !important;
}

/*wyszukiwarka i input*/
#masthead-search-terms, #channel-search.expanded .search-field, #masthead-search-term { 
background: #abcae1!important; /* Old browsers */
background: -moz-linear-gradient(top, #abcae1 0%, #c3d9e7 38%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#abcae1), color-stop(38%,#c3d9e7))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #abcae1 0%,#c3d9e7 38%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #abcae1 0%,#c3d9e7 38%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #abcae1 0%,#c3d9e7 38%)!important; /* IE10+ */
background: linear-gradient(to bottom, #abcae1 0%,#c3d9e7 38%)!important; /* W3C */
border-radius: 15px !important;
}
.yt-uix-form-input-fluid .yt-uix-form-input-text, .yt-uix-form-input-fluid .yt-uix-form-input-textarea {
background: #abcae1!important; /* Old browsers */
background: -moz-linear-gradient(top, #abcae1 0%, #c3d9e7 38%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#abcae1), color-stop(38%,#c3d9e7))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #abcae1 0%,#c3d9e7 38%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #abcae1 0%,#c3d9e7 38%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #abcae1 0%,#c3d9e7 38%)!important; /* IE10+ */
background: linear-gradient(to bottom, #abcae1 0%,#c3d9e7 38%)!important; /* W3C */
border-radius: 15px !important;
}
.gssb_a {
background: #4794B4 !important;
}
#masthead-search {
max-width: 900px !important;
}

/*developers, about, informations, advertise etc.*/
#yt-microsite .yt-card-light, .yt-card-light .yt-card-title a, #yt-microsite-features .yt-microsite-spotlight, .yt-advertise-whyitworks-header,
.yt-advertise-cta-container, .yt-tile-static, .yt-tile-visible, .yt-tile-default:hover, #yt-sidebar li.selected a, .html5-compatibility-table li  {
background: #4794B4 !important;
}
.yt-nav-dark, #yt-main-nav-container { 
background: #599ECE !important;
}
#yt-main-nav a:active, #yt-main-nav a.active { 
background: #4794B4 !important;
}
#yt-main-nav a:hover, #yt-sidebar li a:hover  { 
background-color: #9BC5FF !important;
color: #ffffff !important;
}

/*progress bars*/
.ytp-load-progress  {
background: #bce0ee !important; 
}
.ytp-play-progress {
background: #b8e1fc!important; /* Old browsers */
background: -moz-linear-gradient(top, #b8e1fc 0%, #a9d2f3 35%, #90bae4 42%, #90bcea 47%, #90bff0 50%, #6ba8e5 51%, #a2daf5 68%, #bdf3fd 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#b8e1fc), color-stop(35%,#a9d2f3), color-stop(42%,#90bae4), color-stop(47%,#90bcea), color-stop(50%,#90bff0), color-stop(51%,#6ba8e5), color-stop(68%,#a2daf5), color-stop(100%,#bdf3fd))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #b8e1fc 0%,#a9d2f3 35%,#90bae4 42%,#90bcea 47%,#90bff0 50%,#6ba8e5 51%,#a2daf5 68%,#bdf3fd 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #b8e1fc 0%,#a9d2f3 35%,#90bae4 42%,#90bcea 47%,#90bff0 50%,#6ba8e5 51%,#a2daf5 68%,#bdf3fd 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #b8e1fc 0%,#a9d2f3 35%,#90bae4 42%,#90bcea 47%,#90bff0 50%,#6ba8e5 51%,#a2daf5 68%,#bdf3fd 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, #b8e1fc 0%,#a9d2f3 35%,#90bae4 42%,#90bcea 47%,#90bff0 50%,#6ba8e5 51%,#a2daf5 68%,#bdf3fd 100%)!important; /* W3C */
}

/*html5 player*/
.light-theme .html5-player-chrome, .html5-player-chrome  {
background: #cedbe9!important; /* Old browsers */
background: -moz-linear-gradient(top, #cedbe9 0%, #aac5de 17%, #6199c7 31%, #419ad6 32%, #3a84c3 40%, #4bb8f0 46%, #3a8bc2 66%, #26558b 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#cedbe9), color-stop(17%,#aac5de), color-stop(31%,#6199c7), color-stop(32%,#419ad6), color-stop(40%,#3a84c3), color-stop(46%,#4bb8f0), color-stop(66%,#3a8bc2), color-stop(100%,#26558b)) !important; /* Chrome,Safari4+ */
