background: -o-linear-gradient(top, #cedbe9 0%,#aac5de 17%,#6199c7 31%,#419ad6 32%,#3a84c3 40%,#4bb8f0 46%,#3a8bc2 66%,#26558b 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #cedbe9 0%,#aac5de 17%,#6199c7 31%,#419ad6 32%,#3a84c3 40%,#4bb8f0 46%,#3a8bc2 66%,#26558b 100%) !important; /* IE10+ */
background: linear-gradient(to bottom, #cedbe9 0%,#aac5de 17%,#6199c7 31%,#419ad6 32%,#3a84c3 40%,#4bb8f0 46%,#3a8bc2 66%,#26558b 100%) !important; /* W3C */
}

/*scrollbars*/
.yt-scrollbar-dark ::-webkit-scrollbar-thumb  {
background: #41a9d0 !important;
}
.yt-scrollbar-dark ::-webkit-scrollbar-track {
background: #2F7591 !important;
}

/*share*/
.watch7-hovercard,.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-input-textarea  {
background: #4794B4 !important;
}

/*komentarze full*/
#watch-response {
background: #4794B4 !important;
}
#watch-response-header {
border: none !important;
-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}
#watch-response-content {
border: none !important;
}













}

