css:
@-moz-document url-prefix("https://www.pandora.com/"), url-prefix("http://www.pandora.com/") {
html {
    overflow-y: auto ;
}

body {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

#brandingBar {
    background: #101010;
}

#brandingBar .middlecolumn {
    color: #FFF ;
}

#brandingBar .rightcolumn {
    color: #FFF ;
}

#brandingBar .rightcolumn a {
    color: #FFF ;
}

#brandingBar .rightcolumn .user_activator:hover {
    background: rgba(255,255,255,0.1) ;
}

.stationChangeSelectorNoMenu p {
    width: auto ;
    max-width: none ;
}

#playerBar {
    background-image: url('http://i.imgur.com/xiqQHH3.png');
    border-style: none;
}

#searchPopupNavPosition {
    left: 80px ;
}

#mainContainer {
    background: radial-gradient(ellipse at center,  #7b7b7b 30%,#000000 70%) ;
    /* Old browsers */
    /* FF3.6+ */
    /* Chrome,Safari4+ */
    /* Chrome10+,Safari5.1+ */
    /* Opera 12+ */
    /* IE10+ */
    /* W3C */
    height: 44px;
}

.contentContainer .top {
    background: #151515;
    border-top: 1px solid #505050;
}

.contentnav {
    background: #151515;
}

.stationContent {
    border-right: 1px solid #d0d0d0;
}

.stationListItem.selected .options {
    background: #CCC ;
}

.btn_bg_sort {
    color: #FFF ;
    text-shadow: 1px 1px 0 #333 ;
    background: radial-gradient(ellipse at center,  #7b7b7b 0%,#5b5b5b 100%) ;
    /* Old browsers */
    /* FF3.6+ */
    /* Chrome,Safari4+ */
    /* Chrome10+,Safari5.1+ */
    /* Opera 12+ */
    /* IE10+ */
    /* W3C */
}

.btn_bg_sort:hover {
    background: radial-gradient(ellipse at center,  #9b9b9b 0%,#7b7b7b 100%) ;
    /* Old browsers */
    /* FF3.6+ */
    /* Chrome,Safari4+ */
    /* Chrome10+,Safari5.1+ */
    /* Opera 12+ */
    /* IE10+ */
    /* W3C */
}

.btn_bg_sort.selected {
    background: radial-gradient(ellipse at center,  #5b5b5b 0%,#3b3b3b 100%) ;
    /* Old browsers */
    /* FF3.6+ */
    /* Chrome,Safari4+ */
    /* Chrome10+,Safari5.1+ */
    /* Opera 12+ */
    /* IE10+ */
    /* W3C */
}

#stationSortDate {
    border-top: 1px solid #999 ;
}

#stationSortAlpha {
    border-top: 1px solid #999 ;
    border-left: 1px solid #999 ;
}

.slidesForeground .slides .stationSlides {
    left: 220px ;
    width: 100% ;
}

.slidesBackground {
    width: 100% ;
    background: #1b1b1b ;
}

.slidesBackground img {
    opacity: 0 ;
}

.slidesBackground div img {
    opacity: 0 ;
}

#currentSlideArrow {
    opacity: 0 ;
    width: 0 ;
    height: 0 ;
}

#trackInfo {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

#trackInfo .info {
}

#trackInfo .info a {
    color: #FFF ;
}

.fxCol-cont-5 {
    background: none ;
}

.fxCol-cont-1 {
    background: none ;
}

#trackInfoButtons {
    display: none
;
}

.btn_bg {
    text-shadow: 1px 1px #000 ;
}

.dropdownmenu ul {
    border-radius: 0 ;
    background: #5b5b5b ;
}

.dropdownmenu ul li:hover a {
    background: #6b6b6b ;
}

.dropdownmenu ul li a {
    color: #fff ;
}

#trackDetail a {
    color: #DDD ;
}

.detail .heading {
    color: #FFF ;
    text-shadow: 1px 1px 0 #3b3b3b ;
}

.detail .itemContent {
    color: #FFF ;
}

.detail .divider {
    border-bottom: 1px solid #CCC ;
}

#demo1 a img {
    display: none;
}

div.dots {
    display: none;
}

#promobox {
    display: none;
}

div.platformPromo.view-3.clearfix {
    display: none;
}

ul.row {
    display: none;
}

div.footerContainer.clearfix {
    display: none;
}

#main {
    margin-top: -19px;
}

.adSupported-layout #adLayout, .adSupported-layout .contentContainer, div#mainContentContainer.contentContainer div.content div#mainContent div.home div.homeRightSide div.slidesBackground div img {
    width: 100% ;
}

div.home.fxCol-cont-1.col-prop-1.view-2 {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

div.button.btn_bg.autoshareButton.on {
    display: none;
}

div.button.btn_bg.buyButton {
    display: none;
}

div.stationNameText {
    padding: 0;
}

div.stationContent {
    height: 100%;
}

#playerBar .progress .remainingTime, #playerBar .progress .elapsedTime {
    color: #e0e0e0;
}

#playerBar .playButton a:link, #playerBar .playButton a:visited {
    background: url("http://i.imgur.com/4uunWq3.png") no-repeat scroll left top transparent;
}

#playerBar .playButton a:hover {
    background: url("http://i.imgur.com/ONULBlI.png") no-repeat scroll left top transparent;
}

#playerBar .pauseButton a:link, #playerBar .pauseButton a:visited {
    background: url("http://i.imgur.com/ZIh2GyR.png") no-repeat scroll left top transparent;
}

#playerBar .pauseButton a:hover {
    background: url("http://i.imgur.com/xa242Wv.png") no-repeat scroll left top transparent;
}

#playerBar .skipButton a:link, #playerBar .playButton a:visited {
    background: url("http://i.imgur.com/5YHFcw7.png") no-repeat scroll left top transparent;
}

#playerBar .skipButton a:hover {
    background: url("http://i.imgur.com/WjgxU2k.png") no-repeat scroll left top transparent;
}

#playerBar .thumbDownButton a:link, #playerBar .thumbDownButton a:visited {
    background: url("http://i.imgur.com/QHKhxBZ.png") no-repeat scroll left top transparent;
}

#playerBar .thumbDownButton.indicator, #playerBar .thumbDownButton.indicator a:link, #playerBar .thumbDownButton.indicator a:visited, #playerBar .thumbDownButton a:hover {
    background: url("http://i.imgur.com/A66RVBW.png") no-repeat scroll left top transparent;
}

#playerBar .thumbUpButton a:link, #playerBar .thumbUpButton a:visited {
    background: url("http://i.imgur.com/3q4yLeL.png") no-repeat scroll left top transparent;
}

#playerBar .thumbUpButton.indicator, #playerBar .thumbUpButton.indicator a:link, #playerBar .thumbUpButton.indicator a:visited, #playerBar .thumbUpButton a:hover {
    background: url("http://i.imgur.com/2cXVTAV.png") no-repeat scroll left top transparent;
}

#playerBar .volumeBackground, .welcome .outer_bg, .lightbox_bg.toastItem, .backstage .show_more, .backstage .stationButtons, .backstage .sample, .deletable, .comment_form, #currentSlideArrow {
    filter: url("data:image/svg+xml;utf8,<svg ><filter id='grayscale'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale");
    -webkit-filter: grayscale(100%);
}

#addArtistSeed.addButton, .slidesBackground.unselectable.clearfix img {
    visibility: hidden;
}

.slides.unselectable {
    padding-top: 8px;
    padding-bottom: 6px;
}

.welcome .outer_bg {
    box-shadow: none;
}

.backstage {
    background: url('http://i.imgur.com/Oc7ycmM.gif') repeat-x scroll 0 -690px #FFF;
}

.backstage a, .backstage h2, .backstage h1, .backstage .show_more, .station .content .section .list .col1 h3 {
    color: #101010;
}

.slide .thumbContainer .thumbUp a:link, .slide .thumbContainer .thumbUp a:visited {
    background: url("http://i.imgur.com/3q4yLeL.png")no-repeat scroll 0% 0% transparent;
}

.slide .thumbContainer .thumbUp.indicator a:link, .slide .thumbContainer .thumbUp.indicator a:visited {
    background: url("http://i.imgur.com/3q4yLeL.png")no-repeat scroll 0% 0% transparent;
}

.slide .thumbContainer .thumbDown a:link, .slide .thumbContainer .thumbDown a:visited {
    background: url("http://i.imgur.com/QHKhxBZ.png")no-repeat scroll 0% 0% transparent;
}

.slide .thumbContainer .thumbDown.indicator a:link, .slide .thumbContainer .thumbDown.indicator a:visited {
    background: url("http://i.imgur.com/A66RVBW.png")no-repeat scroll 0% 0% transparent;
}

#mainContentContainer.contentContainer {
    width: 100%;
    margin: 0 auto 0 auto;
}

#splash {
    background-color: #282828;
    background-image: url('http://i.imgur.com/6G07FeX.jpg');
    background-repeat: repeat-x;
    background-attachment: fixed;
    background-position: top center;
}

.slidesForeground, .slidesBackground {
    background-color: #101010;
    background-image: linear-gradient(to bottom, #101010 0%, #282828 100%);
}

img.bottom {
    visibility: hidden;
}

.slide {
    background: #282828;
    box-shadow: 2px 2px 3px rgba(0,0,0,0.5);
}

.slidesForeground .slides_left_scroll, .slidesForeground .slides_right_scroll, #user_menu_dd, .searchPopupContainer .content .footer {
    filter: url("data:image/svg+xml;utf8,<svg ><filter id='grayscale'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale");
    -webkit-filter: grayscale(100%);
}

.contentnav .home_icon, .contentnav .friends_icon, .contentnav .myprofile_icon {
    text-transform: uppercase;
    width: 100px;
    font-size: 0.9em;
    line-height: .7em;
    filter: url("data:image/svg+xml;utf8,<svg ><filter id='grayscale'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale");
    -webkit-filter: grayscale(100%);
}

.tip_box {
    border-radius: 6px 6px 0 0;
}

.stationListHolder {
    background: #d0d0d0;
}

.stationNameText {
    color: #101010;
    text-shadow: 1px 1px 0 #ffffff;
    font-size: 1.1em;
}

.stationName li:hover {
    background: #cccccc;
}

.stationNameText.shared {
    background: url("http://i.imgur.com/m6j7v4t.png") no-repeat scroll 0px 2px transparent;
}

#detailContainer {
    text-align: justify;
}

.stationListItem .options {
    color: #505050;
    border: none;
    background: #ffffff;
}

.stationListItem .option.addVariety {
    border-right: 1px solid #d0d0d0;
}

#thumbup.thumb, #thumbdown.thumb, .slide img.noart, #playerBar .progressBar, #trackInfoButtons .buttons, #stationSortButtons.clearfix, .stationContent #promobox .dots, .jspContainer, .platformPromo.view-3 {
    filter: url("data:image/svg+xml;utf8,<svg ><filter id='grayscale'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale");
    -webkit-filter: grayscale(100%);
}

#trackInfoButtons, .view-3 {
    background: #e0e0e0;
    border-bottom: 1px solid #d0d0d0;
}

.stationContent #promobox {
    border-bottom: 1px solid #d0d0d0;
    border-top: 1px solid #d0d0d0;
}

.home, .stationContent #promobox .dots {
    background: #f0f0f0;
}

#footer .legal, #footer a {
    color: #b0b0b0;
}

.detail .heading, .detail a {
    color: #101010;
}

.detail .itemContent .showMoreLyrics, .detail .itemContent .showMoreAbout {
    background-image: url("http://i.imgur.com/7Chzpzg.png");
}

.lightbox_bg.stationSelectorPopup, .lightbox_bg.addVarietyPopup {
    filter: url("data:image/svg+xml;utf8,<svg ><filter id='grayscale'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale");
    -webkit-filter: grayscale(100%);
}

.spinnerContainer #splash_spinner {
    position: relative;
    background: url(http://i.imgur.com/k5M5ARY.gif) no-repeat;
    width: 40px;
    height: 40px;
    z-index: 10001;
}

div.slides.unselectable {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

div.slidesBackground.unselectable.clearfix {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

div.homeRightSide {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

div.home {
    background-color: #151515;
}

#rightColumnDivider {
    display: none;
}

a.upgradeLink {
    display: none;
}

div.heading {
    color: #ffffff;
}

a.profile_tab {
    background-color: #4d4d4d;
    color: #ffffff;
}

span.metadata.count {
    color: #ffffff;
}

div.backstage.col-prop-1.clearfix {
    background: url(http://i.imgur.com/6G07FeX.jpg)
;
}

div.sample a span {
    color: #ffffff;
}

div.song_name.main a {
    color: #ffffff;
}

div.newsfeed_text.clearfix {
    color: #ffffff;
}

span.newsfeed_song_name a {
    color: #ffffff;
}

a.facebookName {
    color: #ffffff;
}

span.station_name a {
    color: #ffffff;
}

span.album_name a {
    color: #ffffff;
}

a.like_undo {
    color: #ffffff;
}

h3.artist_name.main.hed-5 a {
    color: #ffffff;
}

span.similar.meta-related-text.space-b-1 a {
    color: #ffffff;
}

a.like_likethis {
    color: #ffffff;
}

h1.facebookName {
    color: #ffffff;
}

div.profile_permissions.profile_public {
    color: #ffffff;
}

div.user_message {
    color: #ffffff;
}

div.feed_promo_img {
    display: none;
}

div.feed_title.hed-1 {
    color: #ffffff;
}

div.track_detail_close {
    display: none;
}

div.divider.last {
    display: none;
}

}
