  .ac-tooltip, .ac-holder {
    background: none;
  }
  .ac-frame {
    padding: 20px 15px 3px 12px;
    width: 300px;
    background: #000;
    border-radius: 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
  }
}
