css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soundcloud.com") {
  
  body {
    background: none !important;
  }
  
  .stream__list {
    margin-top: 15px !important;
  }
  
  .l-container.l-content {
    padding-top: 20px !important;
  }
  
  h1.header__logo {
    background: none !important;
  }
  
  .l-fluid-fixed .l-sidebar-right {
    display: none !important;
  }
  
  .l-container {
    width: 95% !important;
  }

  .sc-border-light-right {
    border-right: none !important;
  }
  
  .l-fluid-fixed .l-main {
    margin: 0px 5% 0px 5% !important;
  }
  /*.right {
    margin-right: 1% !important;
  }*/
  .headerSearch .quicksearch {
    width: 100px !important;
  }
  article.sidebarModule:nth-child(6){
    display: none !important;
  }
  .g-main-scroll-area{
    margin-top: 15px !important;
  }
  .listenNetworkTop__inner {
    padding: 34px 0px 0px 5%;
    width: 100%;
   }
}
