/* to be used with the addon "Throbber Restorer" version 1.1.1- signed 
https://addons.mozilla.org/en-US/firefox/addon/throbber-restored/?src=api
*/

@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

@-moz-document url("chrome://browser/content/browser.xul") {
/* the normal image is transparent you have a blank space, 
change the opacity to 0.6 or 0.7 or 0.8 to see a fire still image  */	
    
    #navigator-throbber {-moz-transform: scale(1.15) !important;
opacity: 0.5 !important; 
list-style-image: url(data:image/png;base64,);

transition: opacity 0.5s ease-in-out !important;
transition-delay: 0s !important;
			height: 18px !important;
width: 22px !important;	
padding-top: 14px !important;       
margin-left: 4px !important;
margin-right: 4px !important;    
    }
/* the loding image is a reflective flame Apng */
    
	#navigator-throbber[loading] {
		list-style-image: url(data:image/png;base64,);
height: 18px !important;
width: 22px !important;	
padding-top: 14px !important;
-moz-transform: scale(1.24) !important;
image-rendering: auto !important;
opacity: 0.9 !important;      
transition: opacity 0.5s ease-in-out!important;
transition-delay: 0s !important;        
}
 .tab-throbber[progress], .tab-throbber[busy]
{
	list-style-image: url(data:image/gif;base64,) !important;
padding-bottom: 3px!important;

}   
}
