@-moz-document url("about:newtab") {

.newtab-cell{
    width: auto !important;  
    height: auto !important;}

/* START ---  In this section, you will have an option to adjust the SIZE AND POSITION OF YOUR GRID & THUMBNAILS on the screen, and also expand to unlimited thumbnails (meaning you can SCROLL THE THUMBNAILS on the screen) */ 

.newtab-side-margin{
    max-width: 150px !important;}    /*default margin (per SIDE):  300px, author's choice: 150px  */ 
#newtab-margin-top{
    max-height: 0px !important;}   /*default margin (TOP):   80px, author's: 0px  */ 
#newtab-margin-bottom{
    max-height: 0px !important;}   /*default margin (BOTTOM):  100px, author's: 0px  */ 

.newtab-cell{
    max-height: auto !important;  
    min-height: 0 !important;}      

#newtab-scrollbox {
    background-color: black!important;
    background: -moz-linear-gradient(top,rgba(0,0,0,.05) , rgba(0,0,0,0.0) 90%),
 
    url("http://2.bp.blogspot.com/_a5mK001oySU/TSREVkN5xPI/AAAAAAAAu44/y3h_7OwEGGU/s1600/5.jpg")    /* <= Want to CHANGE THE BACKGROUND ? Copy/paste a the web address between the quote signs, or even better download the image file to your disc first and write the pathway instead (because the page will load the image much faster from an internal disc)  */
                                                                                                    
    
    center center no-repeat !important;
    background-size: cover!important; 
    border-top: none !important;}



   
    
    [href*="firstaddress.co.uk"] .newtab-thumbnail,                                          
    [href*="secondaddress.com"] .newtab-thumbnail,                                         
    [href*="thirdaddress.org"] .newtab-thumbnail {                                    
    background: rgba(0,0,0,.05)
    url("http://wallpaper.address.com/pathway1/pathway2/uniquefilename.jpg")
    center center no-repeat !important; background-size: cover!important;}
    
   
    [href*="uniqueaddress.com"] .newtab-thumbnail {                                
    background: rgba(0,0,0,.05)
    url("http://wallpaper.address.com/pathway1/pathway2/uniquefilename.jpg")
    center center no-repeat !important; background-size: cover!important;}       
  
.newtab-cell{
    background-color:rgba(0,0,0,.05)!important;
    border-radius: 4px !important;
    border: 1px solid rgba(220,220,220,.13)  !important; border-top: 1px solid rgba(255,255,255,.21) !important;
    box-shadow: 1px 1px 5px rgba(0,0,0,.6) !important;}

/* FIREFOX LOGO on empty thumbnails */

.newtab-cell:empty {
    background: -moz-linear-gradient(top,rgba(0,0,0,.05) , rgba(0,0,0,0.0) 90%),
    url(data:image/png;base64,)
    center center no-repeat !important;}

.newtab-thumbnail, .newtab-site {
    border: 2px solid transparent !important;
    border-radius: 4px !important;
    box-shadow:   0px 0px 1px 0px rgba(255,255,255,.05)!important;}

.newtab-thumbnail{
    opacity: 0.5 !important;}   

.newtab-cell:hover .newtab-thumbnail{ 
    opacity: 1 !important;
    background-color:rgba(194,203,203,.03)!important;
    border-radius: 4px!important;
    border: 0px solid rgba(255,255,255,1) !important;
    box-shadow:  inset -1px -1px 1px rgba(229,255,238,.15),  1px 1px 4px 0px rgba(0,0,0,.99), inset 1px 1px 1px rgba(255,255,255,.3)!important;
    -moz-transform: scale(1.02,1.035) !important;}

.newtab-title {
    color: white !important;
    text-shadow: 1px 2px 1px #000!important;
    background-color:  transparent !important;
    background-image: -moz-linear-gradient(top,  rgba(95,107,114,.7) 0%, rgba(194,203,203,0.5) 95%, rgba(251,253,253,0.05) 100%), 
    -moz-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.25))!important;
    font-weight: bold !important;
    padding-left: 12px !important;
    padding-bottom: 0px !important;
    border: 1px solid;
    -moz-border-top-colors:  rgba(50,62,69,.1) rgba(80,92,99,0)!important;
    -moz-border-right-colors: rgba(60,72,79,0.7) rgba(110,122,129,0)!important;
    -moz-border-left-colors:  rgba(60,72,79,0.7) rgba(90,100,90,0) !important;
    -moz-border-bottom-colors:  rgba(0,0,0,0.9)rgba(224,250,233,0) !important;
    border-radius: 25px 25px 6px 6px!important;
    -moz-margin-start:5px!important;
    -moz-margin-end:5px!important;
    margin-bottom: 4px !important;}

.newtab-title:hover,  .newtab-cell:hover .newtab-title{
    border: 1px solid;
    -moz-border-top-colors: rgba(235,235,235,.8)!important;
    -moz-border-right-colors: rgba(75,75,75,.2) rgba(150,160,150, 1)!important;
    -moz-border-left-colors: rgba(75,75,75,.2) rgba(150,160,150,0.0) !important;
    -moz-border-bottom-colors:  rgba(40,40,40,0.8)rgba(224,250,233,.0) !important;
    -moz-margin-start:2px!important;
    -moz-margin-end:2px!important;
    color: white !important;
    background-color: transparent !important;
    background-image: -moz-linear-gradient(top,rgba(220,225,227,.75) , rgba(50,59,59,0.4) 100%), 
    -moz-linear-gradient(left, rgba(255,255,255,.3), rgba(0,9,9,.05) 15%, rgba(0,9,9,.05) 85%,rgba(255,255,255,.45))!important;
    font-size: 1.15em !important;
    padding-left: 10px !important;
    padding-bottom: 1px !important;
    margin-bottom: 3px !important;}
 
.newtab-cell:active .newtab-thumbnail {-moz-transform: scale(.9915,.975) !important; border: 1px solid transparent !important; box-shadow: none !important;}
.newtab-cell .newtab-control-pin[pinned], .newtab-cell .newtab-control-block:-moz-locale-dir(ltr) {-moz-transform: scale(.5,.5) !important; margin: 2px!important;}
.newtab-cell:active .newtab-control-pin[pinned], .newtab-cell:active .newtab-control-block:-moz-locale-dir(ltr) {-moz-transform: scale(.4,.4) !important;}
.newtab-cell:active .newtab-title {-moz-transform: scale(.96,.96) !important;}

form#NTGForm {padding-top: 12px !important; height: 40px !important; margin-top:-35px !important;
    
    /* CONVEX frame */
border-bottom: 1px solid rgba(55,55,55,.4) !important; border-top: 1px solid rgba(70,70,70,.3) !important;
    box-shadow: 1px 1px 5px rgba(50,50,50,.6) !important;
    background: -moz-linear-gradient(top,  rgba(50,50,50,0.15) 0%, rgba(255,255,255,0.16) 40%, rgba(50,50,50,0.2) 100%)!important; }
 
input#NTGinput {width: 400px !important; height: 20px !important; -moz-appearance:none !important; position:relative !important; padding-left: 5px!important; padding-right: 5px!important;
    border: 2px solid;
    -moz-border-top-colors:  rgba(255,255,255,.08) rgba(0,0,0,.5)!important;
    -moz-border-right-colors: rgba(255,255,255,.2) rgba(0,0,0,.1)!important;
    -moz-border-left-colors:  rgba(255,255,255,.08) rgba(0,0,0,.3)!important;
    -moz-border-bottom-colors:  rgba(255,255,255,.2) rgba(0,0,0,.1)!important;
    box-shadow: 0px 0px 10px rgba(255,255,255,.1), inset 0px -2px 30px rgba(0,0,0,.1) !important; background: -moz-linear-gradient(bottom, transparent, rgba(0,0,0,.05)) !important;  border-radius: 4px !important;
    color: rgba(240,240,240,1)!important; font-size: 14px !important; font-weight: bold !important;  text-shadow: 1px 1px 1px #000!important;}
input#NTGsubmit {height: 28px !important; -moz-appearance:none !important; position:relative !important; margin-left: 10px !important; padding-left: 10px!important; padding-right: 10px!important; 
    border: 2px solid;
    -moz-border-bottom-colors:  rgba(155,155,155,.1) rgba(0,0,0,.5)!important;
    -moz-border-left-colors: rgba(50,50,50,.25) rgba(255,255,255,.3)!important;
    -moz-border-right-colors:  rgba(155,155,155,.1) rgba(0,0,0,.35)!important;
    -moz-border-top-colors:  rgba(50,50,50,.25) rgba(255,255,255,.3)!important;
    box-shadow: 0px 0px 10px rgba(255,255,255,.1), inset 0px -2px 30px rgba(0,0,0,.1) !important; background: -moz-linear-gradient(bottom, rgba(255,255,255,.05), rgba(255,255,255,.2)) !important;  border-radius: 4px !important;
    color: #FF9900 !important; font-size: 14px !important; text-shadow: 1px 1px 1px #111, 0px 0px 1px #888!important; font-size: 14px !important; font-weight: bold !important;}
input#NTGsubmit:active {
    border: 2px solid;
    -moz-border-top-colors:  rgba(255,255,255,.15) rgba(0,0,0,.5)!important;
    -moz-border-right-colors: rgba(255,255,255,.3) rgba(0,0,0,.1)!important;
    -moz-border-left-colors:  rgba(255,255,255,.15) rgba(0,0,0,.3)!important;
    -moz-border-bottom-colors:  rgba(255,255,255,.3) rgba(0,0,0,.1)!important;
    color: #FF7700!important; text-shadow: 1px 1px 1px #000, 0px 0px 1px #FF0000!important; background: -moz-linear-gradient(bottom, transparent, rgba(0,0,0,.1)) !important;}
label#NTGlabel{margin-right: 5px !important; color: rgba(200,200,200,1)!important; font-weight: bold !important;  text-shadow: 1px 1px 1px #000!important;}

    }
