@-moz-document domain("s.weibo.com") {
.S_content_l {background:#fff;width:730px;border-radius: 15px;}
.WB_global_nav .gn_header{width:710px;padding-right:180px;}
.gn_title,.gn_logo_v2 {display:none}
.gn_title[node-type=home]{display:block}      
.S_content_r,
.other_link.clearfix,
#base_scrollToTop
{display:none !important;} 
 .S_plwrap   {background-image:none;}   
.S_weibo,
.S_WB,
.S_person,
.S_pic,
.S_app,
 .S_main, .S_weibo .S_main_bg 
 { background-color: #0099B9;background-image: url('http://ww3.sinaimg.cn/large/70bde138jw1egqx7fvegbg21jk070weu.gif'); background-repeat: repeat-x;background-position: center 0px;border-radius: 0px;} 
.S_content{background-image:none;background-color:#FaFaFa;border-radius: 15px;}  
.S_wrap{margin-left:200px;}    
.S_plwrap{width:730px;}
.feed_list .content {font-size: 16px;line-height: 26px;}  
 .search_input .searchInp_auto{background-color:#FaFaFa;} 
