.tabbrowser-tabs
{
margin: 3px 0 0px 0 !important;
line-height: 29px !important;
}

.tabbrowser-tabs .tabbrowser-tab,
.tabbrowser-tabs .tabbrowser-arrowscrollbox .tabs-newtab-button
{
-moz-appearance: none !important;
padding: 0px !important;
vertical-align: bottom !important;
border-left: 2px solid !important;
border-top: 2px solid !important;
border-right: 2px solid !important;
border-bottom: 0px none !important;
-moz-box-sizing: border-box !important;
-moz-border-left-colors: threedshadow rgba(255, 255, 255, 0.7) !important;
-moz-border-top-colors: threedshadow rgba(255, 255, 255, 0.7) !important;
-moz-border-right-colors: threedshadow rgba(255, 255, 255, 0.7) !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

.tabbrowser-tabs .tabbrowser-tab,
.tabbrowser-tabs .tabbrowser-arrowscrollbox .tabs-newtab-button
{
height: 25px !important;
min-height: 25px !important;
margin: 2px 0px 1px 0px !important;
}

.tabbrowser-tabs .tabbrowser-tab:not([selected="true"]):hover
{
height: 26px !important;
min-height: 26px !important;
margin: 1px 0px 1px 0px !important;
}

.tabbrowser-tabs .tabbrowser-tab[selected="true"]
{
height: 28px !important;
min-height: 28px !important;
margin: 0px 0px 0px 0px !important;
}

