@-moz-document domain("narutoforums.com") {
/** Removes the "Your Ad Here Links - Golden Circle */    
* {
    border: none !important;
    }
div#pagenav_menu.vbmenu_popup + div { 
    display: none !important;
    }
body {
   	background: #1e202e !important;
     color: #454545 !important;
    margin: 5px 10px 10px !important;
    padding: 0 !important;
}
.page {
    background: #181822 !important;
    background-image: url('http://i.imgur.com/hIGz0Rw.jpg') !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-position: 0% 0% !important;  
    background-size: auto !important;
    background-origin: padding-box !important;
    min-width: 950px;
    max-width: 100%;
    min-height: 20%;
    max-height: 80%;
    resize: both;
    width: 950px;
    margin-left: auto;
    margin-right: auto;
}
a:link, body_alink {
    color: #d2d8f7 !important;
}
a:visited, body_avisited {
    color: #d2d8f7 !important;
}
a:hover, a:active, body_ahover {
    color: #677fec !important;
}
.tborder {
    background: none repeat scroll 0 0 #000000 !important;
    color: #454545 !important;
}
.tcat {
    background-image: url("http://i.imgur.com/DJxcDOg.png") !important;
    color: #f2f1f4 !important;
}

/** header */

.thead {
    background: none repeat scroll 0 0 #284f66 !important;
    background: url('http://i.imgur.com/vFVxBOr.png') !important;
    color: #ffffff !important;
}
.thead a:link, .thead_alink {
}
.thead a:visited, .thead_avisited {
    color: #f0e7e6 !important;
}
.thead a:hover, .thead a:active, .thead_ahover {
    color: #677fec !important;
}

/** footer */

.tfoot {
    background: none repeat scroll 0 0 #284f66 !important;
    color: #ffffff !important;
}
.tfoot {
    background: none repeat scroll 0 0 #1e1e1e !important;
    color: #ffffff !important;
}
.tfoot a:link, .tfoot_alink {
    color: #ffffff !important;
}

.tfoot a:visited, .tfoot_avisited {
    color: #ffffff !important;
}

.tfoot a:hover, .tfoot a:active, .tfoot_ahover {

    color: #677fec !important;

}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover {
    color: #677fec !important;
}
.wysiwyg {
    color: #ffffff !important;
    padding: 0 !important;
}

.wysiwyg a:link, .wysiwyg_alink {
    color: #454545 !important;
}
.wysiwyg a:visited, .wysiwyg_avisited {
    color: #454545 !important;
}
.wysiwyg a:hover, .wysiwyg a:active, .wysiwyg_ahover {
    color: #454545 !important;
}
.time {
    color: #02aad0 !important;
}
.highlight {
    color: #FF0000 !important;
}
legend {
    color: #ffa584 !important;
}

/** CP */

.vbmenu_control {
    background: none repeat scroll 0 0 #793571 !important;
    color: #FFFFFF !important;
    padding: 3px 6px !important;
    white-space: nowrap !important;
}
.vbmenu_control a:link, .vbmenu_control_alink {
    color: #FFFFFF !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited {
    color: #FFFFFF !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover {
    color: #FFFFFF !important;
}
.vbmenu_popup {
    background: none repeat scroll 0 0 #361837 !important;
    border: 1px solid #0B198C !important;
    color: #361837 !important;
}
.vbmenu_option {
    background: none repeat scroll 0 0 #553656 !important;
    color: #e6e6f2 !important;
    cursor: pointer !important;
    white-space: nowrap !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
    color: #ffffff !important;
    text-decoration: none !important;
}
.vbmenu_hilite {
    background: none repeat scroll 0 0 #676ea3 !important;
    color: #ccccff !important;
    cursor: pointer !important;
    white-space: nowrap !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink {
    color: #ccccff !important;
    text-decoration: none !important;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited {
    color: #ccccff !important;
    text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover {
    color: #dbc3ff !important;
    text-decoration: none !important;
}


.vbmenu_popup {
    background: none repeat scroll 0 0 #361837 !important;
    color: #000000 !important;
    border: 1px solid #d5cece !important;

}

.vbmenu_option a:link, .vbmenu_option_alink {
    color: #d2d7f3 !important;
    text-decoration: none !important;

}

.vbmenu_option a:visited, .vbmenu_option_avisited {
    color: #d2d7f3 !important;
    text-decoration: none !important;
}

.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
    color: #361837 !important;
    text-decoration: none !important;
}
td.thead, th.thead, div.thead {
    padding: 4px !important;
}
.pagenav td {
    padding: 2px 4px !important;
}
.shade, a.shade:link, a.shade:visited {
    color: #777777 !important;
}
a.shade:active, a.shade:hover {
    color: #FF4400 !important;
    text-decoration: underline !important;
}
.tcat .shade, .thead .shade, .tfoot .shade {
    color: #DDDDDD !important;
}
.fieldset {
    margin-bottom: 6px !important;
}
center tr:nth-child(1) td {
    display: none !important;
    }
    
.alt1, .alt1Active {

    background: none repeat scroll 0 0 #553656 !important;
    color: #b8cbf7 !important;

}

.alt2, .alt2Active {
    background: none repeat scroll 0 0 #361837 !important;
    color: #b8cbf7 !important;
}



